INSERT INTO `authors` (`id`, `au_name`, `au_email`, `au_img`, `au_description`, `creation_date`, `update_date`) VALUES
(1, 'Emily Brontë', 'contact@emilybronte.com', '', 'Emily Brontë là một tiểu thuyết gia và nhà thơ người Anh, người đã viết một cuốn tiểu thuyết duy nhất, Wuthering Heights (1847), một tác phẩm giàu trí tưởng tượng về niềm đam mê và sự căm ghét lấy bối cảnh ở vùng đồng hoang Yorkshire. Nó đã nhận được những đánh giá khủng khiếp khi xuất bản lần đầu nhưng được coi là một trong những cuốn tiểu thuyết hay nhất bằng tiếng Anh.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(2, 'Jane Austen', 'contact@janeausten.co.uk', '', 'Jane Austen (16 tháng 12 năm 1775 – 18 tháng 7 năm 1817) là một tiểu thuyết gia người Anh chủ yếu được biết đến với sáu cuốn tiểu thuyết, ngầm diễn giải, phê bình và bình luận về tầng lớp quý tộc người Anh đổ bộ vào cuối thế kỷ 18.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(3, 'J. K. Rowling', 'info@jkrowling.com', '', 'J.K. Rowling là tác giả người Anh, người đã tạo ra bộ truyện Harry Potter nổi tiếng và được giới phê bình đánh giá cao (bảy cuốn sách được xuất bản từ năm 1997 đến 2007), kể về một đứa trẻ mồ côi cô đơn phát hiện ra rằng mình thực sự là một phù thủy và đăng ký vào Trường Phù thủy và Pháp sư Hogwarts.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(4, 'Virginia Woolf', 'contact@virginiawwoolf.com', '', 'Adeline Virginia Woolf (25 tháng 1 năm 1882 – 28 tháng 3 năm 1941) là một nhà văn người Anh. Bà được coi là một trong những tác giả theo chủ nghĩa hiện đại quan trọng nhất của thế kỷ 20 và là người tiên phong trong việc sử dụng dòng ý thức như một công cụ tường thuật.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(5, 'Mary Shelley', 'contact@maryshelley.com', '', 'Mary Wollstonecraft Shelley (30 tháng 8 năm 1797 – 1 tháng 2 năm 1851) là một tiểu thuyết gia người Anh, người đã viết tiểu thuyết Gothic Frankenstein; hoặc, The Modern Prometheus (1818), được coi là ví dụ ban đầu của khoa học viễn tưởng.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(6, 'Ernest Hemingway', 'contact@ernesthemingway.com', '', 'Ernest Hemingway (21 tháng 7 năm 1899-02 tháng 7 năm 1961) được coi là một trong những nhà văn có ảnh hưởng nhất của thế kỷ 20. Nổi tiếng với tiểu thuyết và truyện ngắn, ông cũng là một nhà báo và phóng viên chiến trường tài ba. Phong cách văn xuôi đặc trưng của Hemingway—đơn giản và thoải mái—đã ảnh hưởng đến một thế hệ nhà văn.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(7, 'Lev Nikolayevich Tolstoy', 'contact@levtolstoy.com', '', 'Tác giả người Nga Leo Tolstoy được coi là bậc thầy về tiểu thuyết hiện thực và là một trong những tiểu thuyết gia vĩ đại nhất thế giới, đặc biệt được biết đến với Anna Karenina và Chiến tranh và Hòa bình. Dao động giữa chủ nghĩa hoài nghi và chủ nghĩa giáo điều, ông khám phá những cách tiếp cận đa dạng nhất đối với trải nghiệm của con người.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(8, 'Fyodor Dostoevsky', 'contact@fyodordostoevsky.com', '', 'Fyodor Dostoyevsky, (sinh ngày 11 tháng 11 [30 tháng 10, Kiểu Cũ], 1821, Moscow, Nga—mất ngày 9 tháng 2 [28 tháng 1, Kiểu Cũ], 1881, St. Petersburg), tiểu thuyết gia và nhà văn viết truyện ngắn người Nga có tâm lý thâm nhập vào những ngóc ngách đen tối nhất trong trái tim con người, cùng với những khoảnh khắc soi sáng vượt trội của ông, đã có ảnh hưởng to lớn đến tiểu thuyết thế kỷ 20.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(9, 'Mark Twain', 'contact@marktwain.com', '', 'Mark Twain là một nhà hài hước, tiểu thuyết gia và nhà văn du lịch người Mỹ. Ngày nay ông được nhớ đến nhiều nhất với tư cách là tác giả của Những cuộc phiêu lưu của Tom Sawyer (1876) và Những cuộc phiêu lưu của Huckleberry Finn (1885). Twain được nhiều người coi là một trong những nhà văn Mỹ vĩ đại nhất mọi thời đại.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(10, 'James Joyce', 'contact@jamesjoyce.com', '', 'James Joyce được biết đến với việc thử nghiệm sử dụng ngôn ngữ và khám phá các phương pháp văn học mới, bao gồm độc thoại nội tâm, sử dụng mạng lưới phức tạp của các biểu tượng song song và phát minh ra các từ, cách chơi chữ và ám chỉ trong tiểu thuyết của ông, đặc biệt là Ulysses (1922) và Finnegans Wake (1939).', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(11, 'Malcolm Gladwell', 'contact@malcolmgladwell.com', '', 'Nhà báo, tác giả và diễn giả người Canada gốc Anh Malcolm Timothy Gladwell được biết đến với những bài báo và cuốn sách xác định, tiếp cận và giải thích những tác động bất ngờ của nghiên cứu khoa học xã hội. Ngoài công việc viết lách, ông còn là người dẫn chương trình podcast của Lịch sử xét lại.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(12, 'Stephen Hawking', 'contact@stephenhawking.com', '', 'Stephen William Hawking (8 tháng 1 năm 1942 – 14 tháng 3 năm 2018) là một nhà vật lý lý thuyết, nhà vũ trụ học và tác giả người Anh, vào thời điểm ông qua đời, ông là giám đốc nghiên cứu tại Trung tâm Vũ trụ lý thuyết tại Đại học Cambridge.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(13, 'Jean-Paul Sartre', 'contact@jeanpaulsartre.com', '', 'Jean-Paul Sartre là một tiểu thuyết gia, nhà viết kịch và triết gia người Pháp. Là một nhân vật hàng đầu trong triết học Pháp thế kỷ 20, ông là người tiêu biểu cho triết lý tồn tại được gọi là chủ nghĩa hiện sinh. Các tác phẩm đáng chú ý nhất của ông bao gồm Buồn nôn (1938), Hữu thể và Hư vô (1943), và Chủ nghĩa hiện sinh và Chủ nghĩa nhân văn (1946).', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(14, 'Vaclav Smil', 'vsmil@cc.umanitoba.ca', '', 'Vaclav Smil là Giáo sư danh dự xuất sắc tại Đại học Manitoba. Ông là tác giả của 40 cuốn sách, trong đó có cuốn sách bán chạy nhất của New York Times Thế giới thực sự hoạt động như thế nào và Năng lượng và nền văn minh, do MIT Press xuất bản. Năm 2010, ông được tạp chí Chính sách đối ngoại vinh danh là một trong 100 nhà tư tưởng toàn cầu hàng đầu.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(15, 'Michio Kaku', 'contact@machiokaku.com', '', 'Michio Kaku là nhà vật lý lý thuyết tại City College of New York, tác giả có sách bán chạy nhất và là nhà phổ biến khoa học nổi tiếng. Ông là người đồng sáng lập lý thuyết trường dây (một nhánh của lý thuyết dây) và tiếp tục nghiên cứu của Einstein nhằm hợp nhất bốn lực cơ bản của tự nhiên thành một lý thuyết thống nhất.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(16, 'Karl Marx', 'contact@karlmarx.com', '', 'Karl Marx là một nhà tư tưởng nổi bật, viết về các chủ đề liên quan đến kinh tế, kinh tế chính trị và xã hội. Sinh ra ở Đức, Marx dành phần lớn thời gian ở London, nơi ông viết nhiều tác phẩm nổi tiếng, trong đó có Tuyên ngôn Cộng sản và Tư bản (Das Kapital).', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(17, 'Albert Camus', 'contact@albertcamus.com', '', 'Albert Camus (1913–1960) là một nhà báo, biên tập viên và biên tập viên, nhà viết kịch và đạo diễn, tiểu thuyết gia và tác giả truyện ngắn, nhà tiểu luận chính trị và nhà hoạt động – và, mặc dù ông đã nhiều lần phủ nhận điều đó, nhưng ông là một triết gia.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(18, 'Simone de Beauvoir', 'contact@simonedebeauvoir.com', '', 'Simone de Beauvoir là một nhà văn, nhà nữ quyền, nhà lý luận xã hội và triết gia hiện sinh người Pháp. Cô được biết đến nhiều nhất với những ý tưởng đột phá xung quanh chủ nghĩa nữ quyền; cuốn sách của cô, Giới tính thứ hai, được cho là đánh dấu sự khởi đầu của làn sóng nữ quyền thứ hai trên toàn cầu.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(19, 'Hannah Arendt', 'contact@hannaharendt.com', '', 'Hannah Arendt (1906–1975) là một trong những triết gia chính trị có ảnh hưởng nhất thế kỷ XX. Sinh ra trong một gia đình người Đức gốc Do Thái, bà buộc phải rời Đức vào năm 1933 và sống ở Paris trong 8 năm tiếp theo, làm việc cho một số tổ chức tị nạn Do Thái.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(20, 'Rachel Carson', 'contact@rachelcarson.com', '', 'Rachel Carson là một nhà sinh vật học người Mỹ nổi tiếng với các bài viết về ô nhiễm môi trường và lịch sử tự nhiên của biển. Cuốn sách của bà, Silent Spring (1962), đã trở thành một trong những cuốn sách có ảnh hưởng nhất trong phong trào môi trường hiện đại và tạo động lực cho việc kiểm soát chặt chẽ hơn các loại thuốc trừ sâu, trong đó có DDT.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(21, 'Nguyễn Nhật Ánh', 'nguyennhatanh@gmail.com', '', 'Nguyễn Nhật Ánh (sinh ngày 7 tháng 5 năm 1955)[1] là một nam nhà văn người Việt Nam. Được xem là một trong những nhà văn hiện đại xuất sắc nhất Việt Nam hiện nay, ông được biết đến qua nhiều tác phẩm văn học về đề tài tuổi trẻ.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(22, 'Nguyễn Ngọc Tư', 'ngngoctucm@hcm.vn', '', 'Nguyễn Ngọc Tư (sinh năm 1976 tại xã Tân Duyệt, huyện Đầm Dơi, tỉnh Cà Mau) là một nhà văn, thành viên Hội nhà văn Việt Nam. Cô được biết đến với tập truyện mang tên Cánh đồng bất tận. Tập truyện nhận giải thưởng của Hội nhà văn Việt Nam năm 2006 và truyện ngắn cùng tên đã được chuyển thể thành phim điện ảnh năm 2010.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(23, 'Thạch Lam', 'contact@thachlam.com', '', 'Nhà văn Thạch Lam – Một cây bút giàu xúc cảm, ông được biết đến là một trong những nhà văn nổi tiếng của nền văn học hiện đại Việt Nam vào giai đoạn những năm 1930 – 1945.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(24, 'Vũ Trọng Phụng', 'contact@vutrongphung.com', '', 'Vũ Trọng Phụng (1912-1939) là một nhà văn, nhà báo nổi tiếng của Việt Nam vào đầu thế kỷ 20. Tuy thời gian cầm bút rất ngắn ngủi, với tác phẩm đầu tay là truyện ngắn Chống nạng lên đường đăng trên Ngọ báo vào năm 1930, ông đã để lại một kho tác phẩm đáng kinh ngạc: hơn 30 truyện ngắn, 9 tập tiểu thuyết, 9 tập phóng sự, 7 vở kịch, cùng một bản dịch vở kịch từ tiếng Pháp, một số bài viết phê bình, tranh luận văn học và hàng trăm bài báo viết về các vấn đề chính trị, xã hội, văn hóa.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(25, 'Nam Cao', 'contact@namcao.com', '', 'Nam Cao (tên khai sinh là Trần Hữu Tri, 29 tháng 10, năm 1915 hoặc 1917 – 30 tháng 11 năm 1951[1]) là một nhà văn, nhà thơ, nhà báo và cũng là một chiến sĩ, liệt sỹ người Việt Nam. Ông là nhà văn hiện thực lớn (trước Cách mạng Tháng Tám), một nhà báo kháng chiến (sau Cách mạng), một trong những nhà văn tiêu biểu nhất thế kỷ 20. Nam Cao có nhiều đóng góp quan trọng đối với việc hoàn thiện phong cách truyện ngắn và tiểu thuyết Việt Nam ở nửa đầu thế kỷ 20.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(26, 'Đặng Hoàng Giang', 'contact@danghoanggiang.com', '', 'Đặng Hoàng Giang là một người ủng hộ, một nhà nghiên cứu về xã hội dân sự và là một nhà nghiên cứu quản trị. Ông đã thiết kế và tham gia nhiều dự án đổi mới về hiệu suất của chính quyền địa phương , tính minh bạch và trách nhiệm giải trình . Ông viết cho nhiều báo chí và thường xuyên xuất hiện trên truyền hình để bàn về các xu hướng xã hội, tư pháp và phát triển. Là một nhà hoạt động xã hội.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(27, 'Tô Hoài', 'contact@tohoai.com', '', 'Tô Hoài (tên khai sinh: Nguyễn Sen; 27 tháng 9 năm 1920 – 6 tháng 7 năm 2014) là một nhà văn Việt Nam. Một số tác phẩm đề tài thiếu nhi của ông được dịch ra ngoại ngữ. Ông được nhà nước Việt Nam trao tặng Giải thưởng Hồ Chí Minh về Văn học – Nghệ thuật Đợt 1 (1996) cho các tác phẩm: Xóm giếng, Nhà nghèo, O chuột, Dế mèn phiêu lưu ký, Núi Cứu quốc, Truyện Tây Bắc, Mười năm, Xuống làng, Vỡ tỉnh, Tào lường, Họ Giàng ở Phìn Sa, Miền Tây, Vợ chồng A Phủ, Tuổi trẻ Hoàng Văn Thụ.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(28, 'Ngô Tất Tố', 'contact@ngotatto.com', '', 'Ngô Tất Tố (1893[1] – 20 tháng 4 năm 1954) là một nhà văn, nhà báo, nhà Nho học, dịch giả và nhà nghiên cứu có ảnh hưởng lớn ở Việt Nam giai đoạn trước 1954.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(29, 'Nguyễn Du', 'contact@nguyendu.com', '', 'Nguyễn Du là một nhà thơ, nhà văn hóa lớn thời Lê mạt Nguyễn sơ ở Việt Nam. Ông được người Việt kính trọng tôn xưng là Đại thi hào dân tộc và được UNESCO vinh danh là Danh nhân văn hóa thế giới. Tác phẩm Truyện Kiều của ông được xem là một kiệt tác văn học, một trong những thành tựu tiêu biểu nhất trong nền văn học trung đại Việt Nam.', '2023-11-17 16:00:40', '2023-11-17 16:00:40'),
(30, 'Bùi Văn Nam Sơn', 'contact@buivannamson.com', '', 'Bùi Văn Nam Sơn là một triết gia, tác giả và dịch giả nổi tiếng và có uy tín tại Việt Nam. Ông đã dịch và hiệu đính một số tác phẩm triết học và khoa học xã hội đã được xuất bản tại Việt Nam.', '2023-11-17 16:00:40', '2023-11-17 16:00:40');
INSERT INTO `blog` (`id`, `blog_title`, `blog_img`, `blog_content`, `blog_desc`, `admin_id`, `creation_date`, `update_date`) VALUES
(1, 'Một trong những sách tâm lý học tội phạm hay, cực ', 'blog2.jpg', 'Cuốn sách về tâm lý học tội phạm này đề cập một góc nhìn không mấy lạ lẫm về tâm trí của các tội phạm, chặn đứng hy vọng về việc trừng trị và cải tạo họ. Tác phẩm này nhấn mạnh sự thật rằng tội phạm thường không thể hiểu rõ và thay đổi. Cuốn sách giải thích cách môi trường sống và gia đình đã góp phần hình thành tính cách tội phạm.\n\nMột phần trong cuốn sách là dành cho việc xem xét tình dục và ma túy trong thế giới tội phạm. Tác giả khẳng định rằng bản chất của con người không dễ dàng thay đổi và rằng đói nghèo, thiếu thốn và bạo lực không thể giải thích hành vi phạm tội. Sách cho thấy cách tư duy của tội phạm phát triển khác biệt so với những người lương thiện.\n\nCuốn sách cũng tập trung vào vấn đề nghiêm trọng của ma túy và tác động tiêu cực của chúng đối với con người thông qua sản xuất và lạm dụng. Sách đánh giá việc sử dụng hình phạt hình sự và nói rõ rằng chúng chỉ hiệu quả khi hiểu được tâm lý cực đoan của tội phạm. Cuối cùng, tác giả rút ra những điểm quan trọng nhất từ cuốn sách.', 'Nhờ cốt truyện hay, nhân vật được xây dựng sâu sắc cùng plot twist bất ngờ, tâm lý tội phạm luôn là chủ đề..', 1, '2023-12-05 16:11:21', '2023-12-05 16:11:21'),
(2, 'Cuốn sách hay nhất mọi thời đại', 'blog3.jpg', 'Cuốn sách đầu tiên mà bạn nên đọc đó là cuốn Đắc Nhân Tâm của Dale Carnegie. Cuốn sách này được mệnh danh là cuốn sách có tầm ảnh hưởng nhất thời đại. Trong nhiều năm qua quyển sách Đắc Nhân Tâm luôn là quyển sách được nhiều người lựa chọn nhất bởi nó mang lại giá trị sâu sắc cho người độc giả.\r\n\r\nĐắc Nhân Tâm mang đến cho độc giả những nguyên tắc đối nhân xử thế trong cuộc sống. Phải đối xử tốt với bản thân mình trước rồi mới học được cách đối xử với mọi người xung quanh. Con người cần thành thật, nhẫn lại, làm việc một cách thẩu hiểu thì mới cảm nhận hết được giá trị của cuộc sống. Từ đó có cách nhìn nhận đánh giá mọi người xung quanh một cách tích cực mới hòa nhập được mọi người.\r\n\r\nMỗi người khi đọc quyển sách này đều có cái nhìn riêng về nguồn cảm hứng mà tác giả muốn truyền đến họ. Mỗi người một suy nghĩ, một hướng đi khác nhau nhưng cuối cùng cái mà tác giả muốn hướng đến cho mọi người là hi vọng và niềm tin vào cuộc sống.\r\n\r\n', 'Sách là kho tàng tri thức vô tận càng đọc kiến thức của chúng ta lại càng trở nên rộng lớn. Kho tàng tri thức...', 8, '2023-12-05 16:11:21', '2023-12-05 16:11:21'),
(3, 'Cuốn sách hay về lịch sử loài người', 'blog1.jpg', 'Với gần 700 trang, cuốn “Súng, vi trùng và thép” được chia làm 4 phần. Phần thứ nhất từ vườn địa đàng đến Cajamarca. Phần 2 viết về sự bành trướng của sản xuất lương thực, thực phẩm. Phần 3 từ lương thực đến súng, vi trùng, thép và phần kết. Mỗi phần đều có những thông tin về lịch sử phát triển của thế giới ở những góc độ khác nhau nhưng xuyên suốt là những lý giải về lịch sử loài người xuất hiện đầu tiên ở châu Phi, sau đó lan ra thế giới. Tác giả cũng đã đặt câu hỏi phải chăng sự khác nhau về phát triển của các quốc gia, cộng đồng các khu dân cư là do tính thông minh di truyền. Rồi qua từng trang sách, ông chứng minh điều đó là sai. Tác giả khẳng định nguyên nhân chính tạo ra sự khác biệt trình độ phát triển của con người xuất phát từ điều kiện địa lý, sinh học vùng đất đó chứ không phải do trí thông minh di truyền của từng tộc người. Ông cũng có những lý giải vì sao con người lại sợ vi trùng...', '\"Súng, vi trùng và thép\" (Guns, Germs and Steel) của Giáo sư Jared Diamond (nhà khoa học người Mỹ)...', 1, '2023-12-05 16:29:30', '2023-12-05 16:29:30'),
(4, 'Chuyện của người lính thời hậu chiến', 'blog4.jpg', 'Bản lĩnh của người lính từng vào sinh, ra tử nơi chiến trường có được tiếp nối khi trở về với cuộc sống đời thường không? Biết bao câu hỏi được đặt ra đối với chúng ta khi muốn tìm hiểu về cuộc sống của những người lính đã từng đi qua chiến tranh. Cuốn “Chúng tôi thời hậu chiến” của tác giả Vũ Công Chiến được lưu trữ tại Thư viện tỉnh sẽ giải đáp phần nào những thắc mắc này.\r\n\r\nVới giọng văn điềm đạm, tác giả đã kể cho bạn đọc nghe những câu chuyện vui buồn của người lính sau chiến tranh. Đó là những ngày trở về trong sự hân hoan, chào đón của người thân. Đó là những thách thức lớn khi người lính phải bắt tay vào xây dựng kinh tế, lo cuộc sống gia đình. Những câu chuyện đời thường, chuyện làm ăn của những người lính chân chất, mộc mạc được tác giả kể với giọng văn lúc vui, lúc buồn.', 'Cuộc chiến tranh chống Mỹ đã kết thúc 46 năm....', 8, '2023-12-05 16:29:30', '2023-12-05 16:29:30'),
(5, 'Cuốn sách chữa lành cho người trẻ có những vết thương tâm lý', 'blog5.jpg', 'Tác giả Minh Đào của cuốn sách Rồi cũng sẽ qua nhận định: “Giống như bao người lớn khác, cách chúng ta thường đối xử với những vết thương lòng là trốn tránh, phủ nhận, đè nén, nín lặng... để vấn đề ngừng phát ra tiếng kêu. Ta yên lặng nhưng không hề yên bình”.\r\n\r\nChính vì thế, tác phẩm của anh đi sâu vào việc bóc tách vấn đề và đưa ra các nút gỡ cho những vết thương tâm lý của người trẻ. Cuốn sách được chia làm 4 chương, theo trình tự của một khóa tham vấn tâm lý.', 'Cuốn sách \"Rồi cũng sẽ qua\" giúp bạn trẻ đối diện với những tiếng nói chỉ trích từ tâm trí...', 1, '2023-12-05 16:50:52', '2023-12-05 16:50:52'),
(6, 'Nguồn gốc các loài - Cuốn sách khoa học làm rung chuyển cả thế giới', 'blog6.jpg', 'SÁCH THEO CHỦ ĐỀ\r\nTop 9 cuốn sách khoa học hay bổ ích và đáng đọc nhất\r\nTrong sự phát triển của xã hội, khoa học đóng một vai trò vô cùng quan trọng được xem là nền tảng để thúc đẩy sự phát triển của nhân loại. Chúng ta tìm hiểu về khoa học không chỉ mang đến một kiến thức khổng lồ về nguồn gốc, sự ra đời mà còn dẫn lối đến những sự thật thú vị về cuộc sống xung quanh. Bởi vậy, các cuốn sách khoa học luôn hấp dẫn đối với bạn đọc và nếu bạn đang hiểu các cuốn sách về khoa học thì top 9 cuốn sách khoa học hay bổ ích và đáng đọc nhất được chúng tôi tổng hợp dưới đây chắc chắn sẽ vô cùng thú vị và hữu ích dành cho bạn.\r\n\r\nTop 9 cuốn sách khoa học hay bổ ích và đáng đọc nhất\r\n\r\n1. Nguồn gốc các loài - Cuốn sách khoa học làm rung chuyển cả thế giới\r\n“Nguồn gốc các loài” được tác giả Charles Darwin cho ra đời vào năm 1859, đây được xem là cuốn sách khoa học tạo nên địa chấn đối với toàn cầu, từ ngày đầu ra mắt, cuốn sách đã bán hết 1250 bản in. Cuốn sách là kết quả của sự quan sát của tác giả trong chuyến khảo sát gần 5 năm từ năm 1831 đến 1836 cùng với đó là kinh nghiệm thực tiễn của tác giả trong hai mươi năm sau đó, ông tập trung vào việc biến đổi luận hay sự tiến hóa của muôn loài. Sách của ông đã được xuất bản nhiều lần, liên tục sửa đổi và cập nhật cho đến khi ông mất. \r\n\r\nCuốn sách khoa học tạo nên địa chấn đối với toàn thế cầu\r\nCuốn sách khoa học tạo nên địa chấn đối với toàn thế cầu\r\nTrước khi cuốn sách “Nguồn gốc các loài” ra đời, con người tồn tại nhưng không hề ý thức được rằng mình từ đâu mà có, hay tổ tiên là ai; họ dành niềm tin quá vào Chúa trời và cho rằng mọi thứ đều là sự sắp đặt của Đấng Tôi Cao trên kia. Bởi vậy, khi cuốn sách của Charles Darwin sắp sửa ra đời đã gây nên một làn sóng phản đối mạnh mẽ, “một nửa dân số nước Anh có thể đứng nhìn ông chết cháy trên cột sắc không chút tiếc thương”. Nhưng ông vẫn quyết định xuất bản cuốn sách và nó đã bán hết trong ngày đầu tiên. Darwin đã kết luận rằng các loài không phải là những thực thể bất biến từ các sáng tạo riêng biệt mà biến đổi từ loài này sang loài sáng, toàn bộ sinh vật luôn có sự vận động và tiến hóa. Cơ sở cho việc tiến hóa là sự đấu tranh sinh tồn giữa các cá thể ở từng loại, loại bỏ nhiều cá thể trong đó và dẫn tới sự sống sót của dạng thích nghi nhất. Đây là sự chọn lọc diễn ra tự động trong tự nhiên (natural selection). Qua tích lũy, các biến đổi được chọn lọc tự nhiên được giữ lại, dần dần biến đổi loài, từ đó mà giới sinh vật dần dần có dạng như hiện nay. ', 'Nguồn gốc các loài” được tác giả Charles Darwin cho ra đời vào năm 1859, đây được xem...', 1, '2023-12-05 16:50:52', '2023-12-05 16:50:52'),
(9, 'Hiệu ứng chim mồi của tác giả Hạo Nhiên – Quốc Khánh.', 'blog9.jpg', 'Sẽ có rất nhiều bạn đọc sẽ thắc mắc rằng tại sao tác giả lại đặt tên sách là Hiệu ứng chim mồi? Nếu bạn đã từng chơi bẫy chim thì đều sẽ biết chim mồi là gì. Khi bắt chim, chúng ta thường đặt một con chim trước miệng chiếc bẫy để thu hút, khiêu khích cũng như tạo hiệu ứng để gây sự chú ý cho những con chim mà mình muốn bẫy. Trong kinh doanh, bán hàng cũng như vậy, chúng ta cũng sẽ tạo ra những hiệu ứng thu hút người mua. Hiệu ứng chim mồi hay còn gọi là hiệu ứng ưu thế bất cân xứng, một hiệu ứng rất thường được dân kinh doanh dùng trong định giá. Để đơn gian, bạn có thể hiểu như sau: khi bán hàng, người bán sẽ tạo ra những hiệu ứng để thu hút người mua một cách triệt để. Người mua lúc này chỉ có hai lựa chọn, một là mua hàng, hai là để mất nó mãi mãi, đều này sẽ giúp thôi thúc người mua nhanh chóng đưa ra quyết định và chốt được đơn hàng.\r\n\r\nTrái ngược với nội dung, về hình thức cuốn sách nhận được khá nhiều lời nhận xét không mấy tích cực.  một số ít người đọc nhận xét rằng họ không thích bìa của quyển sách bởi vì bìa màu xanh lá cây, logo nhà xuất bản cũng màu xanh lá cây luôn. Chất lượng giấy bình thường trong khi giá của quyển sách khá cao, và họ cũng rất khó khăn trong việc mua sách. Đây cũng được coi là một điểm trừ trong việc tạo ấn tượng đầu tiên cho các bạn đọc', 'Quyển sách này là một trong số ít những cuốn sách marketing...', 8, '2023-12-05 16:50:52', '2023-12-05 16:50:52'),
(10, 'Dám Thất Bại - Billi P.S. Lim', 'blog10.jpg', 'Trong cơn mưa nặng hạt đang kéo dài âm ỉ ngoài kia, còn gì thú vị hơn khi ta ngồi bên khung cửa sổ, nhâm nhi tách trà nóng và đọc quyển sách ta yêu thích. Giống như có ma lực, quyển sách đã cuốn hút tôi. Không chút đắn đo, suy nghĩ, tôi liền chọn nó. Tuy phần thiết kế bìa sách hơi đơn giản và màu sắc không được rực rỡ, nhưng bố cục lại vô cùng rõ ràng, tinh tế. Cuốn sách không dày, chỉ có 200 trang thôi, với bìa sách tông màu xanh nước biển khá hợp với sở thích của tôi. Dòng chữ \"\r\n<strong>DÁM THẤT BẠI</strong>\r\n” được làm nổi bật ngay giữa bìa, tựa tiếng Anh của cuốn sách này là “\r\n<strong>DARE TO FAIL</strong>', 'Ắt hẳn chúng ta thường nghe, thường đọc về những tấm gương thành công...', 8, '2023-12-05 16:56:34', '2023-12-05 16:56:34');
INSERT INTO `books` (`id`, `isbn_13`, `title`, `book_description`, `price`, `sale_off`, `views`, `fm_id`, `au_id`, `pub_id`, `creation_date`, `updation_date`) VALUES
(1, '9780141439556', 'Đồi gió hú', 'Giới thiệu về Đồi Gió Hú cuốn tiểu thuyết bắt nguồn từ trang viên Yorkshire trên cánh đồng hoang của câu chuyện. Câu chuyện tập trung vào tình yêu trọn vẹn, nồng nàn nhưng cuối cùng cũng phải chịu số phận giữa Catherine Earnshaw và Heathcliff, cũng như việc niềm đam mê không được giải quyết này cuối cùng đã hủy hoại họ và những người xung quanh như thế nào.', 200.000, 0, 36, 1, 1, 1, '2023-11-18 19:32:25', '2023-11-18 19:32:25'),
(2, '9780141439556', 'Wuthering Heights', 'Wuthering Heights is the first and only novel by the English author Emily Brontë, initially published in 1847 under her pen name Ellis Bell.', 300.000, 0, 0, 1, 1, 4, '2023-11-18 19:32:25', '2023-11-18 19:32:25'),
(3, '9780141439518', 'Kiêu hãnh và định kiến', 'Kiêu hãnh và định kiến là tác phẩm nổi tiếng nhất của nhà văn Anh Jane Austen. Tiểu thuyết được viết từ năm 1796 đến năm 1797 và xuất bản năm 1813. Câu chuyện nói về tình yêu và hôn nhân của tầng lớp quý tộc nhỏ tại Anh vào đầu thế kỷ 19.', 200.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:03', '2023-11-18 19:33:03'),
(4, '9780141439518', 'Pride and Prejudice', 'Pride and Prejudice is an 1813 novel of manners by English author Jane Austen. The novel follows the character development of Elizabeth Bennet, the protagonist of the book, who learns about the repercussions of hasty judgments and comes to appreciate the difference between superficial goodness and actual goodness.', 300.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:03', '2023-11-18 19:33:03'),
(5, '9780141439587', 'Emma', 'Jane Austen là một tiểu thuyết gia người Anh. Bà nổi tiếng với sáu cuốn tiểu thuyết lấy bối cảnh giới địa chủ trung lưu Anh vào cuối thế kỷ 18. Cốt truyện của Austen thường phản ánh tình cảnh phụ thuộc của người phụ nữ vào hôn nhân như là cứu cánh duy nhất để đảm bảo vị thế xã hội và lợi ích vật chất.', 200.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:03', '2023-11-18 19:33:03'),
(6, '9780141439587', 'Emma', 'Emma is a novel written by English author Jane Austen. It is set in the fictional country village of Highbury and the surrounding estates of Hartfield, Randalls and Donwell Abbey, and involves the relationships among people from a small number of families.', 300.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:53', '2023-11-18 19:33:53'),
(7, '9780141439662', 'Sense and Sensibility', 'Sense and Sensibility is the first novel by the English author Jane Austen, published in 1811. It was published anonymously; By A Lady appears on the title page where the author\'s name might have been. It tells the story of the Dashwood sisters, Elinor and Marianne as they come of age.', 300.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:53', '2023-11-18 19:33:53'),
(8, '9798861522953', 'Thuyết phục', 'Thuyết phục là cuốn tiểu thuyết cuối cùng được hoàn thành bởi tác giả người Anh Jane Austen. Nó được xuất bản vào ngày 20 tháng 12 năm 1817, cùng với Tu viện Northanger, sáu tháng sau khi bà qua đời, mặc dù trang tiêu đề ghi ngày 1818.', 200.000, 0, 0, 1, 2, 1, '2023-11-18 19:33:53', '2023-11-18 19:33:53'),
(9, '9798861522953', 'Persuasion: Novel', 'Persuasion is the last novel completed by the English author Jane Austen. It was published on 20 December 1817, along with Northanger Abbey, six months after her death, although the title page is dated 1818.', 300.000, 0, 0, 1, 2, 1, '2023-11-18 19:35:05', '2023-11-18 19:35:05'),
(10, '9781408855652', 'Harry Potter và Hòn đá Phù thủy', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:35:05', '2023-11-18 19:35:05'),
(11, '9781338878936', 'Harry Potter và phòng chứa bí mật', 'Harry Potter và tên tù nhân ngục Azkaban là một quyển tiểu thuyết thuộc thể loại giả tưởng kỳ ảo được viết bởi văn sĩ người Anh J. K. Rowling, đây cũng là tập thứ 3 trong bộ truyện Harry Potter. Quyển sách theo chân Harry Potter, cậu phù thủy nhỏ, trong năm học thứ ba của mình tại Trường Phù thủy và Pháp sư Hogwarts.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:35:05', '2023-11-18 19:35:05'),
(12, '9781338815283', 'Harry Potter Và Tên Tù Nhân Ngục Azkaban', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:35:05', '2023-11-18 19:35:05'),
(13, '9780439139595', 'Harry Potter Và Chiếc Cốc Lửa', 'Harry Potter và chiếc cốc lửa là một quyển sách thuộc thể loại giả tưởng kỳ ảo được viết bởi tác giả người Anh J. K. Rowling và đây cũng là phần thứ tư trong bộ tiểu thuyết Harry Potter.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(14, '9780439358064', 'Harry Potter Và Hội Phượng Hoàng', 'Harry Potter và hội Phượng hoàng là quyển thứ 5 trong bộ sách Harry Potter của nhà văn J. K. Rowling. Quyển này được đồng loạt xuất bản vào ngày 21 tháng 6 năm 2003 tại Anh, Hoa Kỳ, Canada, Úc và một vài quốc gia khác. Trong ngày đầu tiên xuất bản, nó đã bán được gần 7 triệu cuốn riêng tại Hoa Kỳ và Anh.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(15, '9781408855652', 'Harry Potter Và Hoàng Tử Lai', 'Harry Potter và Hoàng tử lai là quyển sách thứ sáu trong bộ sách giả tưởng nổi tiếng Harry Potter của tác giả J.K. Rowling. Cũng như các quyển trước, nó cũng trở thành một trong những bestseller của năm nó xuất bản.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(16, '9780545010221', 'Harry Potter Và Bảo Bối Tử Thần', 'Harry Potter và Bảo bối Tử thần là cuốn sách thứ bảy và cũng là cuối cùng của bộ tiểu thuyết giả tưởng Harry Potter của nhà văn Anh J.K. Rowling. Nguyên bản tiếng Anh được phát hành đồng thời tại Anh, Hoa Kỳ, Canada và một số nước khác vào ngày 21 tháng 7 năm 2007.', 200.000, 0, 0, 1, 3, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(17, '9781408855652', 'Harry Potter and the Philosopher\'s Stone', 'Harry Potter and the Philosopher\'s Stone is a fantasy novel written by British author J. K. Rowling. The first novel in the Harry Potter series and Rowling\'s debut novel, it follows Harry Potter, a young wizard who discovers his magical heritage on his eleventh birthday, when he receives a letter of acceptance to Hogwarts School of Witchcraft and Wizardry. Harry makes close friends and a few enemies during his first year at the school and with the help of his friends, Ron Weasley and Hermione Granger, he faces an attempted comeback by the dark wizard Lord Voldemort, who killed Harry\'s parents, but failed to kill Harry when he was just 15 months old.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(18, '9781338878936', 'Harry Potter and the Chamber of Secrets', 'The Chamber is flanked with towering pillars entwined with carved wood, and a tall statue of Salazar Slytherin is at the far end. The basilisk rests within the mouth of the statue, possibly symbolising Salazar Slytherin\'s ability to speak Parseltongue. Sometime before 1992, the chamber flooded with water.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(19, '9781338815283', 'Harry Potter and the Prisoner of Azkaban', 'Harry Potter and the Prisoner of Azkaban is a fantasy novel written by British author J. K. Rowling and is the third in the Harry Potter series. The book follows Harry Potter, a young wizard, in his third year at Hogwarts School of Witchcraft and Wizardry.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(20, '9780439139595', 'Harry Potter and the Goblet of Fire', 'The novels follow Harry Potter, an 11yearold boy who discovers he is the son of famous wizards and will attend Hogwarts School of Witchcraft and Wizardry. Harry learns of an entire society of wizards and witches. He befriends several Hogwarts students and teachers along his journey.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(21, '9780439358064', 'Harry Potter and the Order of the Phoenix', 'It follows Harry Potter\'s struggles through his fifth year at Hogwarts School of Witchcraft and Wizardry, including the surreptitious return of the antagonist Lord Voldemort, O.W.L. exams, and an obstructive Ministry of Magic.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(22, '9781408855652', 'Harry Potter and the HalfBlood Prince', 'owling, J. K. In this book, Harry Potter learns a lot about Lord Voldemort\'s past, and Harry Potter prepares for the final battle against his nemesis with the help of Headmaster Dumbledore. But in that time, Voldemort returns to power, and makes a plan to destroy Harry.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(23, '9780545010221', 'Harry Potter and the Deathly Hallows', 'The Deathly Hallows is about Harry Potter and his friends finding ways to destroy Voldemort. They learn that even good contains a bit of evil, and vise versa. Even though the trio faces many difficulties, they persevere.', 300.000, 0, 0, 1, 3, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(24, '9780143136132', 'Bà Dalloway', 'Bà Dalloway là một cuốn tiểu thuyết của Virginia Woolf xuất bản vào ngày 14 tháng 5 năm 1925. Nó kể chi tiết một ngày trong cuộc đời của Clarissa Dalloway, một phụ nữ thuộc tầng lớp thượng lưu hư cấu ở Anh sau Thế chiến thứ nhất. Đây là một trong những tiểu thuyết nổi tiếng nhất của Woolf.', 200.000, 0, 0, 1, 4, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(25, '9780143136132', 'Mrs. Dalloway', 'Dalloway, novel by Virginia Woolf published in 1925. It examines one day in the life of Clarissa Dalloway, an upperclass Londoner married to a member of Parliament. Mrs. Dalloway is essentially plotless; what action there is takes place mainly in the characters consciousness.', 300.000, 0, 0, 1, 4, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(26, '9780143136132', 'Frankenstein', 'Frankenstein: or The Modern Prometheus is an 1818 novel written by English author Mary Shelley. Frankenstein tells the story of Victor Frankenstein, a young scientist who creates a sentient being in an unorthodox scientific experiment.', 300.000, 0, 0, 1, 4, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(27, '9780143131847', 'Frankenstein', 'Frankenstein: hay The Modern Prometheus là một cuốn tiểu thuyết năm 1818 được viết bởi tác giả người Anh Mary Shelley. Frankenstein kể câu chuyện về Victor Frankenstein, một nhà khoa học trẻ, người đã tạo ra một sinh vật có trí tuệ trong một thí nghiệm khoa học không chính thống.', 200.000, 0, 0, 1, 4, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(28, '9780143131847', 'Ông già và biển cả', 'Ông già và Biển cả là một tiểu thuyết ngắn được Ernest Hemingway viết ở Cuba năm 1951 và xuất bản năm 1952. Nó là truyện ngắn dạng viễn tưởng cuối cùng được Hemingway viết và được xuất bản khi ông còn sống. Đây cũng là tác phẩm nổi tiếng và là một trong những tác phẩm đỉnh cao trong sự nghiệp sáng tác của nhà văn.', 200.000, 0, 0, 1, 6, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(29, '9789384225483', 'The Old Man and the Sea', 'The Old Man and the Sea is a 1952 novella written by the American author Ernest Hemingway. Written between December 1950 and February 1951, it tells the story of Santiago, an aging fisherman who catches a giant marlin after a long struggle, but then loses his bounty to sharks.', 300.000, 0, 0, 1, 6, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(30, '9789384225483', 'Chuông nguyện hồn ai', 'Chuông nguyện hồn ai là tiểu thuyết được xuất bản năm 1940 của nhà văn Mỹ Ernest Miller Hemingway. Cuốn tiểu thuyết viết về Robert Jordan, một người Mỹ thuộc Lữ đoàn quốc tế, tham gia chống Phát xít trong cuộc nội chiến Tây Ban Nha.', 200.000, 0, 0, 1, 6, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(31, '9780684803357', 'For Whom the Bell Tolls', 'For Whom the Bell Tolls is a novel by Ernest Hemingway published in 1940. It tells the story of Robert Jordan, a young American volunteer attached to a Republican guerrilla unit during the Spanish Civil War. As a dynamiter, he is assigned to blow up a bridge during an attack on the city of Segovia.', 300.000, 0, 0, 1, 6, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(32, '9780684803357', 'Chiến tranh và hòa bình', 'Chiến tranh và hòa bình là một tiểu thuyết của Lev Nikolayevich Tolstoy, được xuất bản rải rác trong giai đoạn 1865–1869. Tác phẩm được xem là thành tựu văn học xuất sắc nhất của Tolstoy, cũng như một tác phẩm kinh điển của văn học thế giới.', 200.000, 0, 0, 1, 7, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(33, '9780140447934', 'War and Peace', 'War and Peace is a literary work by Russian author Leo Tolstoy. Set during the Napoleonic Wars, the work mixes fictional narrative with chapters discussing history and philosophy. First published serially beginning in 1865, the work was rewritten and published in its entirety in 1869.', 300.000, 0, 0, 1, 7, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(34, '9780140447934', 'Anna Karenina', 'Anna Karenina là một tiểu thuyết của nhà văn Nga Lev Nikolayevich Tolstoy, được đăng tải nhiều kỳ trên tờ báo Ruskii Vestnik từ năm 1873 đến năm 1877 trước khi xuất bản thành ấn phẩm hoàn chỉnh. Anna Karenina được xem như là một đỉnh cao của tiểu thuyết hiện thực.', 200.000, 0, 0, 1, 7, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(35, '9780684803357', 'Anna Karenina', 'Anna Karenina is a novel by the Russian author Leo Tolstoy, first published in book form in 1878. Considered to be one of the greatest works of literature ever written, Tolstoy himself called it his first true novel.', 300.000, 0, 0, 1, 7, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(36, '9780684803357', 'Cái chết của Ivan Ilyich', 'Cái chết của Ivan Ilyich, xuất bản lần đầu năm 1886, là một tiểu thuyết của Leo Tolstoy, được coi là một trong những kiệt tác của tiểu thuyết cuối cùng của ông, được viết ngay sau khi ông cải đạo vào cuối những năm 1870.Cái chết của Ivan Ilyich', 200.000, 0, 0, 1, 7, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(37, '9781548397531', 'A Confession', 'Leo Tolstoy\'s A Confession, published in 1882, is an autobiographical work that deals with the philosophical and religious aspects of the meaning of life. Tolstoy\'s struggle with how to live a meaningful life forms the core of the narrative.', 300.000, 0, 0, 1, 7, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(38, '9780553210354', 'The Death of Ivan Ilyich', 'The Death of Ivan Ilyich, first published in 1886, is a novella by Leo Tolstoy, considered one of the masterpieces of his late fiction, written shortly after his religious conversion of the late 1870s.', 300.000, 0, 0, 1, 7, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(39, '9780486415871', 'Tội ác và hình phạt', 'Tội ác và hình phạt là một tiểu thuyết của nhà văn Nga Fyodor Mikhailovich Dostoyevsky. Tiểu thuyết này cùng với Anh em nhà Karamazov là hai tác phẩm nổi tiếng nhất của Dostoevsky. Tạp chí Times đã bình chọn Tội ác và hình phạt là một trong số những cuốn sách vĩ đại nhất mọi thời đại.', 200.000, 0, 0, 1, 8, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(40, '9780486415871', 'Crime and Punishment', 'Crime and Punishment is a novel by the Russian author Fyodor Dostoevsky. It was first published in the literary journal The Russian Messenger in twelve monthly installments during 1866. It was later published in a single volume.', 300.000, 0, 0, 1, 8, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(41, '9780374528379', 'Anh em nhà Karamazov', 'Anh em nhà Karamazov là tác phẩm cuối cùng của Fyodor Dostoevsky. Fyodor Dostoevsky thuộc loại thiên tài hiếm hoi mà càng về cuối đời thì sự nghiệp sáng tác càng lên tới đỉnh cao hơn. Anh em nhà Karamazov là tác phẩm vĩ đại nhất của ông.', 200.000, 0, 0, 1, 8, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(42, '9780374528379', 'The Brothers Karamazov', 'The Brothers Karamazov, also translated as The Karamazov Brothers, is the last novel by Russian author Fyodor Dostoevsky. Dostoevsky spent nearly two years writing The Brothers Karamazov, which was published as a serial in The Russian Messenger from January 1879 to November 1880.', 300.000, 0, 0, 1, 8, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(43, '9780679734512', 'Ác quỷ', 'Ác quỷ là một cuốn tiểu thuyết của Fyodor Dostoevsky, được xuất bản lần đầu trên tạp chí The Russian Messenger vào năm 1871–72. Nó được coi là một trong bốn kiệt tác được Dostoevsky viết sau khi ông trở về từ cuộc lưu đày ở Siberia, cùng với Tội ác và Trừng phạt, Thằng ngốc và Anh em nhà Karamazov.', 200.000, 0, 0, 1, 8, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(44, '9780679734512', 'Demons (Dostoevsky novel)', 'Demons is a novel by Fyodor Dostoevsky, first published in the journal The Russian Messenger in 1871–72. It is considered one of the four masterworks written by Dostoevsky after his return from Siberian exile, along with Crime and Punishment, The Idiot, and The Brothers Karamazov.', 300.000, 0, 0, 1, 8, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(45, '9781503215672', 'Những cuộc phiêu lưu của Tom Sawyer', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 200.000, 0, 0, 1, 9, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(46, '9781503215672', 'The Adventures of Tom Sawyer', 'The Adventures of Tom Sawyer is an 1876 novel by Mark Twain about a boy growing up along the Mississippi River. It is set in the 1840s in the town of St. Petersburg, which is based on Hannibal, Missouri, where Twain lived as a boy.', 300.000, 0, 0, 1, 9, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(47, '9780143107323', 'Những cuộc phiêu lưu của Huckleberry Finn', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 200.000, 0, 0, 1, 9, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(48, '9780143107323', 'Adventures of Huckleberry Finn', 'Adventures of Huckleberry Finn is a novel by American author Mark Twain, which was first published in the United Kingdom in December 1884 and in the United States in February 1885.', 300.000, 0, 0, 1, 9, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(49, '9780679722762', 'Ulysses', 'Ulysses là tiểu thuyết gồm 3 phần của James Joyce xuất bản lần đầu năm 1922. Với ẩn dụ về sử thi Odysseus, thủ pháp dòng ý thức và nội dung được nén chặt bằng những sự kiện miên man trôi theo suy tưởng của các nhân vật diễn ra chỉ trong một ngày 16 tháng 6 năm 1904, tác phẩm được tạp chí Time đánh giá là một trong số những kiệt tác văn chương lớn nhất thế giới thế kỷ 20.', 200.000, 0, 0, 1, 10, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(50, '9780679722762', 'Ulysses', 'Ulysses is a modernist novel by Irish writer James Joyce. Parts of it were first serialized in the American journal The Little Review from March 1918 to December 1920, and the entire work was published in Paris by Sylvia Beach on 2 February 1922, Joyce\'s fortieth birthday.', 300.000, 0, 0, 1, 10, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(51, '9780140186475', 'Người Dublin', 'Dubliners là một tuyển tập gồm mười lăm truyện ngắn của James Joyce, được xuất bản lần đầu tiên vào năm 1914. Cuốn sách này mô tả một cách tự nhiên về cuộc sống của tầng lớp trung lưu Ireland trong và xung quanh Dublin trong những năm đầu của thế kỷ 20.', 200.000, 0, 0, 1, 10, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(52, '9780140186475', 'Dubliners', 'Dubliners is a collection of fifteen short stories by James Joyce, first published in 1914. It presents a naturalistic depiction of Irish middle class life in and around Dublin in the early years of the 20th century.', 300.000, 0, 0, 1, 10, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(53, '9780316024976', 'Những kẻ xuất chúng', 'Những kẻ ngoài lề: Câu chuyện thành công là cuốn sách phi hư cấu do Malcolm Gladwell viết và được Little, Brown and Company xuất bản vào ngày 18 tháng 11 năm 2008. Trong Outlier, Gladwell xem xét các yếu tố góp phần vào mức độ thành công cao.', 200.000, 0, 0, 1, 11, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(54, '9780141036250', 'Outliers: The Story of Success', 'Outliers: The Story of Success is the nonfiction book written by Malcolm Gladwell and published by Little, Brown and Company on November 18, 2008. In Outliers, Gladwell examines the factors that contribute to high levels of success.', 300.000, 0, 0, 1, 11, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(55, '9780316346627', 'Điểm bùng phát', 'Điểm tới hạn: Những điều nhỏ bé có thể tạo nên sự khác biệt lớn như thế nào là cuốn sách đầu tay của Malcolm Gladwell, được Little, Brown xuất bản lần đầu vào năm 2000. Gladwell định nghĩa điểm tới hạn là thời điểm của khối lượng tới hạn, ngưỡng, điểm sôi.', 200.000, 0, 0, 1, 11, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(56, '9780316346627', 'The Tipping Point: How Little Things Can Make a Big Difference', 'The Tipping Point: How Little Things Can Make a Big Difference is the debut book by Malcolm Gladwell, first published by Little, Brown in 2000. Gladwell defines a tipping point as the moment of critical mass, the threshold, the boiling point.', 300.000, 0, 0, 1, 11, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(57, '9788483468913', 'Lý thuyết về tất cả mọi thứ', 'Trong tác phẩm xuất sắc của nhà vật lý Stephen Hawking, Lược sử thời gian, ông đã trình bày cho chúng ta một cái nhìn mới táo bạo về vũ trụ của chúng ta, nó bắt đầu như thế nào và quan điểm cũ của chúng ta về vật lý cũng như các lý thuyết mệt mỏi về sự hình thành vũ trụ không còn phù hợp như thế nào nữa.', 200.000, 0, 0, 1, 12, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(58, '9788817079761', 'The illustrated theory of everything: The origin and fate of the universe', 'In physicist Stephen Hawking\'s brilliant opus, A Brief History of Time, he presented us with a bold new look at our universe, how it began, and how our old views of physics and tired theories about the creation of the universe were no longer relevant.', 300.000, 0, 0, 1, 12, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(59, '9780553380163', 'Lược sử thời gian', 'Điểm tới hạn: Những điều nhỏ bé có thể tạo nên sự khác biệt lớn như thế nào là cuốn sách đầu tay của Malcolm Gladwell, được Little, Brown xuất bản lần đầu vào năm 2000. Gladwell định nghĩa điểm tới hạn là thời điểm của khối lượng tới hạn, ngưỡng, điểm sôi.', 200.000, 0, 0, 1, 12, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(60, '9780553380163', 'A Brief History of Time', 'A Brief History of Time: From the Big Bang to Black Holes is a book on theoretical cosmology by English physicist Stephen Hawking. It was first published in 1988. Hawking wrote the book for readers who had no prior knowledge of physics.', 300.000, 0, 0, 1, 12, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(61, '9782070257539', 'Buồn nôn', 'Buồn nôn là một tiểu thuyết triết học của triết gia hiện sinh Jean-Paul Sartre, xuất bản năm 1938. Đây là tiểu thuyết đầu tay của Sartre. Cuốn tiểu thuyết diễn ra ở Bouville, một thị trấn tương tự như Le Havre.', 200.000, 0, 0, 1, 13, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(62, '9782070257539', 'Nausea', 'Nausea is a philosophical novel by the existentialist philosopher Jean-Paul Sartre, published in 1938. It is Sartre\'s first novel. The novel takes place in \'Bouville\' a town similar to Le Havre.', 300.000, 0, 0, 1, 13, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(63, '9788532666826', 'Tồn tại và hư vô', 'L\'Être et le Néant là một tác phẩm lớn, hơn 700 trang sách, của Jean-Paul Sartre, một trong những nhà triết học hàng đầu của Pháp thế kỷ 20, một trong những người sáng lập ra Chủ nghĩa hiện sinh. Lúc L\'Être et le Néant ra đời, chẳng mấy người hiểu nó.', 0.000, 0, 0, 1, 13, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(64, '9789754680096', 'Existentialism Is a Humanism', 'Existentialism Is a Humanism is a 1946 work by the philosopher Jean-Paul Sartre, based on a lecture by the same name he gave at Club Maintenant in Paris, on 29 October 1945.', 131.000, 0, 0, 1, 13, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(65, '9780241989692', 'Những Con Số Biết Nói  71 Câu Chuyện Phơi Bày Sự Thật Về Thế Giới Chúng Ta Đang Sống', 'Chứa đựng những thông tin hấp dẫn và những ví dụ đáng nhớ, Numbers Don\'t Lie tiết lộ cách Hoa Kỳ đang dẫn đầu xu hướng tiêu thụ thịt gà ngày càng tăng trên toàn thế giới, việc tiêm chủng mang lại lợi tức đầu tư tốt nhất và tại sao ô tô điện không tuyệt vời như chúng ta nghĩ.', 477.000, 0, 0, 1, 14, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(66, '9780241989692', 'Numbers Don\'t Lie: 71 Stories to Help Us Understand the Modern World', 'An essential guide to understanding how numbers reveal the true state of our world–exploring a wide range of topics including energy, the environment, technology, transportation, and food production. Vaclav Smil\'s mission is to make facts matter.', 387.000, 0, 0, 1, 14, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(67, '9780593297063', 'How the World Really Works: The Science Behind How We Got Here and Where We\'re Going', 'In How the World Really Works: A Scientist\'s Guide to Our Past, Present and Future, Vaclav Smil explores seven fundamental areas that govern human survival and prosperity, covering topics such as food production, energy and globalisation.', 155.000, 0, 0, 1, 14, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(68, '9780307473332', 'Phương Trình Của Chúa - Cuộc Truy Tìm Lý Thuyết Của Vạn Vật', 'Một cuốn sách xuất sắc được viết bởi một nhà truyền thông khoa học bậc thầy. Nếu ai đó có thể làm sáng tỏ những điều thâm thúy của toán học và lý thuyết dây, thì đó chính là Michio Kaku. Trong cuốn sách nhỏ tuyệt vời này, tác giả giải thích bằng những thuật ngữ rõ ràng và đơn giản những đột phá về khái niệm và những câu hỏi chưa được giải đáp - trong việc tìm kiếm một lý thuyết thống nhất vĩ đại của vạn vật.', 180.000, 0, 0, 1, 15, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(69, '9780307473332', 'Tương Lai Nhân Loại', 'Địa cầu hóa Sao Hỏa, viếng thăm các vì sao, sự bất tử và định mệnh bên ngoài Trái Đất\n\n“Theo tôi, nếu không tiến ra không gian, nhân loại sẽ không có tương lai.” –Stephen Hawking\n\nVậy hành trình tiến ra không gian ấy sẽ thuận lợi hay khó khăn? Sẽ cần bao nhiêu thời gian và những công nghệ gì? Nhân loại đã chuẩn bị những gì cho định mệnh đó? Và cuộc sống con người khi ấy sẽ ra sao?', 491.000, 0, 0, 1, 15, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(70, '9780307473332', 'Physics of the Future', 'Based on interview material with more than 300 of the world\'s leading scientists - who are inventing the future in the laboratory - and taking into account the safety principles of technological development, the famous theoretical physicist Michio Kaku describes in detail the revolution in medicine, computers, quantum physics and space travel that is taking place. All this will forever change our lives and the entire course of human civilization.', 414.000, 0, 0, 1, 15, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(71, '9780307473332', 'The Communist Manifesto', 'The Communist Manifesto, originally the Manifesto of the Communist Party, is a political pamphlet written by Karl Marx and Friedrich Engels, commissioned by the Communist League and originally published in London in 1848.', 119.000, 0, 0, 1, 15, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(72, '9780717802418', 'Capital, Volume I', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 177.000, 0, 0, 1, 16, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(73, '9780486477480', 'Capital, Volume II', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 366.000, 0, 0, 1, 16, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(74, '9780853150275', 'Capital, Volume III', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 142.000, 0, 0, 1, 16, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(75, '9780679720201', 'Người xa lạ', 'Người xa lạ là một tiểu thuyết của Albert Camus được viết vào năm 1942. Đây là một tác phẩm lạ thường nói về một người đàn ông bình thường, người mà cuối cùng đã bị tống giam vì tội giết người, và ngồi chờ bị hành hình.', 347.000, 0, 0, 1, 17, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(76, '9780679720201', 'The Stranger', 'The Stranger is a novel by Albert Camus written in 1942. It is an unusual work about an ordinary man, who is eventually imprisoned for murder, and waits to be executed. image.', 122.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(77, '9780525564454', 'Thần thoại Sisyphus', 'Huyền thoại về Sisyphus là một tiểu luận triết học năm 1942 của Albert Camus. Chịu ảnh hưởng của các triết gia như Søren Kierkegaard, Arthur Schopenhauer và Friedrich Nietzsche, Camus giới thiệu triết lý phi lý của mình.', 240.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(78, '9780679720218', 'Dịch hạch', 'Dịch hạch là một cuốn tiểu thuyết nổi tiếng của Albert Camus xuất bản năm 1947. Một vài nhân vật của Dịch hạch đã xuất hiện trong cuốn sổ tay của Camus viết ở Alger năm 1938. Nhưng hơn cả là ở Oran, Algérie từ cuối năm 1940 đến mùa xuân năm 1942, rồi ở Pháp, Camus đã soạn thảo lại cuốn tiểu thuyết của mình.', 333.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(79, '9780679720201', 'The Stranger', 'The Stranger, also published in English as The Outsider, is a 1942 novella written by French author Albert Camus. The first of Camus\' novels published in his lifetime, the story follows Meursault, an indifferent settler in French Algeria, who, weeks after his mother\'s funeral, kills an unnamed Arab man in Algiers.', 447.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(80, '9780525564454', 'The Myth of Sisyphus', 'The Myth of Sisyphus is a 1942 philosophical essay by Albert Camus. Influenced by philosophers such as Søren Kierkegaard, Arthur Schopenhauer, and Friedrich Nietzsche, Camus introduces his philosophy of the absurd.', 495.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(81, '9780679720218', 'The Plague', 'The Plague is a 1947 absurdist novel by Albert Camus. It tells the story from the point of view of a narrator in the midst of a plague sweeping the French Algerian city of Oran. The narrator remains unknown until the start of the last chapter.', 216.000, 0, 0, 1, 17, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(82, '9780307277787', 'Giới tính thứ hai', 'Giới tính hạng hai là cuốn sách của nhà triết học hiện sinh người Pháp Simone de Beauvoir, xuất bản năm 1949. Trong cuốn sách, tác giả thảo luận về cách thức phụ nữ bị đối xử trong suốt chiều dài lịch sử. Beauvoir nghiên cứu và viết cuốn sách trong khoảng 14 tháng, từ năm 1946 đến năm 1949.', 137.000, 0, 0, 1, 18, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(83, '9780307277787', 'The Second Sex', 'The Second Sex is a 1949 book by the French existentialist philosopher Simone de Beauvoir, in which the author discusses the treatment of women in the present society as well as throughout all of history. Beauvoir researched and wrote the book in about 14 months between 1946 and 1949.', 436.000, 0, 0, 1, 18, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(84, '9780143039884', 'Eichmann ở Jerusalem', 'Eichmann ở Jerusalem: Một phóng sự về Sự Tầm Thường của Cái Ác là cuốn sách xuất bản năm 1963 của nhà lý luận chính trị Hannah Arendt.', 166.000, 0, 0, 1, 19, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(85, '9780143039884', 'Eichmann in Jerusalem', 'Eichmann in Jerusalem: A Report on the Banality of Evil is a 1963 book by political thinker Hannah Arendt. Arendt, a Jew who fled Germany during Adolf Hitler\'s rise to power, reported on the trial of Adolf Eichmann, one of the major organizers of the Holocaust, for The New Yorker.', 275.000, 0, 0, 1, 19, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(86, '9780156701532', 'The Origins of Totalitarianism', 'The Origins of Totalitarianism, published in 1951, was Hannah Arendt\'s first major work, wherein she describes and analyzes Nazism and Stalinism as the major totalitarian political movements of the first half of the 20th century.', 335.000, 0, 0, 1, 19, 5, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(87, '9788087888957', 'On Violence', 'An analysis of the nature, causes, and significance of violence in the second half of the twentieth century. Arendt also reexamines the relationship between war, politics, violence, and power.', 469.000, 0, 0, 1, 19, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(88, '9780618249060', 'Mùa xuân vắng lặng', 'Mùa xuân vắng lặng là một cuốn sách về đề tài khoa học môi trường của Rachel Carson. Tác phẩm được xuất bản vào ngày 27 tháng 9 năm 1962 và ghi lại những tác động xấu đến môi trường do sử dụng bừa bãi thuốc bảo vệ thực vật.', 264.000, 0, 0, 1, 20, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(89, '9780618249060', 'Silent Spring', 'Silent Spring is an environmental science book by Rachel Carson. Published on September 27, 1962, the book documented the environmental harm caused by the indiscriminate use of pesticides.', 304.000, 0, 0, 1, 20, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(90, '9780067575208', 'The Sense of Wonder', 'The Sense of Wonder relates Carson\'s intimate account of adventures with her young nephew, Roger, as they enjoy walks along the rocky coast of Maine and through dense forests and open fields, observing wildlife, strange plants, moonlight, and storm clouds and listening to the living music of insects in the underbrush.', 181.000, 0, 0, 1, 20, 4, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(91, '9786041004757', 'Cho tôi xin một vé đi tuổi thơ', 'Cho tôi xin một vé đi tuổi thơ là truyện ngắn của nhà văn Nguyễn Nhật Ánh. Tác phẩm là một trong những sáng tác thành công nhất của ông và nhận được Giải thưởng Văn học ASEAN của năm 2010. Nguyễn Nhật Ánh viết ở mặt sau cuốn sách. Tôi viết cuốn sách này không dành cho trẻ em. Tôi viết cho những ai từng là trẻ em.', 186.000, 0, 0, 1, 21, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(92, '9786041004757', 'Mắt biếc', 'Mắt biếc là tiểu thuyết của nhà văn Nguyễn Nhật Ánh trong loạt truyện viết về tình yêu thanh thiếu niên của tác giả này cùng với Thằng quỷ nhỏ, Cô gái đến từ hôm qua,... Đây được xem là một trong những tác phẩm tiêu biểu của Nguyễn Nhật Ánh.', 249.000, 0, 0, 1, 21, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(93, '9786041000698', 'Tôi thấy hoa vàng trên cỏ xanh', 'Tôi thấy hoa vàng trên cỏ xanh là một tiểu thuyết dành cho thanh thiếu niên của nhà văn Nguyễn Nhật Ánh, xuất bản lần đầu tại Việt Nam vào ngày 9 tháng 12 năm 2010 bởi Nhà xuất bản Trẻ với phần tranh minh họa do Đỗ Hoàng Tường thực hiện.', 400.000, 0, 0, 1, 21, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(94, '9786041004689', 'Cánh đồng bất tận', 'Cánh đồng bất tận là tên một tập truyện ngắn phát hành năm 2005 của Nguyễn Ngọc Tư, đồng thời cũng là tên một truyện trong tập truyện ngắn đó được đăng báo lần đầu cùng năm.', 482.000, 0, 0, 3, 22, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(95, '9786041005167', 'Ngọn đèn không tắt', 'Cuốn sách “Ngọn Đèn Không Tắt” của tác giả Nguyễn Ngọc Tư là một tập truyện ngắn đầy hấp dẫn và thuyết phục về thanh niên hôm nay trên mọi mặt đời sống. Tác phẩm được viết với giọng văn mộc mạc, bình dị và ngôn ngữ đời thường, tạo nên một không khí tự nhiên về màu sắc và hương vị của mảnh đất cuối cùng của tổ quốc.', 253.000, 0, 0, 1, 22, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(96, '9786043943078', 'Hai Đứa Trẻ', 'Cuốn sách tập hợp những truyện ngắn đặc sắc của nhà văn Thạch Lam: Nắng trong vườn, Gió lạnh đầu mùa, Tiếng chim kêu, Đứa con đầu lòng, Bắt đầu, Nhà mẹ Lê, Một cơn giận, Hai đứa trẻ, Đứa con, Trong bóng tối buổi chiều, Cuốn sách bỏ quên, Dưới bóng hoàng lan,...', 190.000, 0, 0, 1, 23, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(97, '9786042198097', 'Hà Nội Băm Sáu Phố Phường', 'Văn học Việt Nam thời xưa có nhiều tác phẩm có giá trị to lớn về mặt nhân văn và nghệ thuật, đã được công nhận và chứng thực qua thời gian. Bộ sách Việt Nam danh tác bao gồm loạt tác phẩm đi cùng năm tháng như: Số đỏ (Vũ Trọng Phụng), Việc làng (Ngô Tất Tố), Gió đầu mùa, Hà Nội băm sáu phố phường (Thạch Lam), Miếng ngon Hà Nội (Vũ Bằng), Vang bóng một thời (Nguyễn Tuân). Hy vọng bộ sách sau khi tái bản sẽ giúp đông đảo tầng lớp độc giả thêm hiểu, tự hào và nâng niu kho tàng văn học nước nhà.', 116.000, 0, 0, 1, 23, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(98, '9780472068043', 'Số Đỏ', 'Văn học Việt Nam thời xưa có nhiều tác phẩm có giá trị to lớn về mặt nhân văn và nghệ thuật, đã được công nhận và chứng thực qua thời gian. Bộ sách Việt Nam danh tác bao gồm loạt tác phẩm đi cùng năm tháng như: Số đỏ (Vũ Trọng Phụng), Việc làng (Ngô Tất Tố), Gió đầu mùa, Hà Nội băm sáu phố phường (Thạch Lam), Miếng ngon Hà Nội (Vũ Bằng), Vang bóng một thời (Nguyễn Tuân). Hy vọng bộ sách sau khi tái bản sẽ giúp đông đảo tầng lớp độc giả thêm hiểu, tự hào và nâng niu kho tàng văn học nước nhà.', 199.000, 0, 0, 1, 24, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(99, '9786049638428', 'Làm Đĩ', 'Làm đĩ là một cuốn tiểu thuyết của nhà văn Việt Nam Vũ Trọng Phụng, tác phẩm được viết vào năm 1936 và xuất bản vào năm 1937. Đây được coi là cuốn tiểu thuyết đầu tiên của Việt Nam đề cập đến vấn đề mại dâm.', 381.000, 0, 0, 1, 24, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(100, '9786045326121', 'Chí Phèo', 'Chí Phèo là một truyện ngắn nổi tiếng của nhà văn Nam Cao viết vào tháng 2 năm 1941. Chí Phèo là một tác phẩm xuất sắc, thể hiện nghệ thuật viết truyện độc đáo của Nam Cao, đồng thời là một tấn bi kịch của một người nông dân nghèo bị tha hóa trong xã hội. Chí Phèo cũng là tên nhân vật chính của truyện.', 169.000, 0, 0, 1, 25, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(101, '9786043724943', 'Lão Hạc', 'Lão Hạc là một truyện ngắn của nhà văn Nam Cao được viết năm 1943. Tác phẩm được đánh giá là một trong những truyện ngắn khá tiêu biểu của dòng văn học hiện thực, nội dung truyện đã phần nào phản ánh được hiện trạng xã hội Việt Nam trong giai đoạn trước Cách mạng tháng Tám.', 311.000, 0, 0, 1, 25, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(102, '9786045342411', 'Bức xúc không làm ta vô can', 'Bức xúc không làm ta vô can chính là cuốn sách đầu tiên về thể loại chính luận mà tác giả luôn theo đuổi, một tác phẩm mang tinh phần phản biện xã hội sâu sắc. Sách được xuất bản năm 2015, là tập hợp những bài bình về các vấn đề trong xã hội, các vấn đề về văn hóa, những hiện tượng xã hội đương thời.', 285.000, 0, 0, 1, 26, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(103, '9786045342411', 'Điểm đến của cuộc đời: đồng hành với người cận tử và những bài học cho cuộc sống', 'Điểm đến của cuộc đời kể lại một hành trình không thể nào quên cùng những người cận tử. Dấn thân vào một thế giới của những bi kịch và tổn thất khổng lồ, của phẩm giá và lòng tự trọng trong hoàn cảnh khắc nghiệt, của sự phản bội và sợ hãi, của tình yêu mãnh liệt và hy vọng khôn nguôi, tóm lại, của tất cả những gì thuộc về con người, ở mức độ dữ dội nhất, tác giả muốn đi tìm câu trả lời cho thôi thúc nội tâm: ta nên ứng xử thế nào trước cái chết, và sự chết có thể dạy ta điều gì cho cuộc sống?', 296.000, 0, 0, 1, 26, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(104, '9786042262279', 'Dế Mèn phiêu lưu ký', 'Dế Mèn phiêu lưu ký là tác phẩm văn xuôi đặc sắc và nổi tiếng nhất của nhà văn Tô Hoài viết về loài vật, dành cho lứa tuổi thiếu nhi. Ban đầu truyện có tên là Con dế mèn do Nhà xuất bản Tân Dân, Hà Nội phát hành năm 1941. Sau đó, được sự ủng hộ nhiệt tình của nhân dân, Tô Hoài viết thêm truyện Dế Mèn phiêu lưu ký.', 379.000, 0, 0, 1, 27, 1, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(105, '8935212327985', 'Tắt đèn', 'Tắt đèn là một trong những tác phẩm văn học tiêu biểu nhất của nhà văn Ngô Tất Tố. Đây là một tác phẩm văn học hiện thực phê phán với nội dung nói về cuộc sống khốn khổ của tầng lớp nông dân Việt Nam đầu thế kỉ XX dưới ách đô hộ của thực dân Pháp.', 444.000, 0, 0, 1, 28, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(106, '9786043943283', 'Lều chõng', 'Lều chõng là một tiểu thuyết phóng sự của nhà văn Ngô Tất Tố, ra mắt độc giả lần đầu ngày 10 tháng 3 năm 1939 tại Hà Nội.', 292.000, 0, 0, 1, 28, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(107, '8936067604634', 'Truyện Kiều', 'Đoạn trường tân thanh, thường được biết đến với cái tên đơn giản là Truyện Kiều, là một truyện thơ của đại thi hào Nguyễn Du. Đây được xem là truyện thơ nổi tiếng nhất và xét vào hàng kinh điển trong văn học Việt Nam, tác phẩm được viết bằng chữ Nôm, theo thể lục bát, gồm 3.254 câu.', 86.000, 0, 0, 1, 29, 3, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(108, '8935081111609', 'Hiện Tượng Học Tinh Thần', 'Có thể khẳng định một cách chắc chắn rằng Hiện tượng học Tinh thần là một trong các tác phẩm khó nhất và tham vọng nhất trong kho tàng triết văn thế giới. Tuy thế, bất chấp nhận định của W. Winderlband hơn 100 năm trước “Giống người đã có thể hiểu được quyển Hiện tượng học Tinh thần của Hegel đang trên đà tuyệt chủng. Giờ đây, ngay số người đã chịu khó đọc tác phẩm ấy từ đầu đến cuối chắc cũng chỉ còn đếm được trên đầu ngón tay mà thôi” thì Hiện tượng học Tinh thần vẫn cứ tiếp tục có ma lực dị thường. Và muốn tìm hiểu về Hegel không còn cách nào khác chúng ta phải bắt đầu với Hiện tượng học Tinh thần – tác phẩm lớn đầu tiên và cũng là tác phẩm thiên tài nhất của ông.', 270.000, 0, 0, 1, 30, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(109, '8935081112804', 'Trò Chuyện Triết Học Tập 1', 'Ở nơi chợ búa ồn ã bán mua, nơi chợ đời bụi bặm, triết học héo lánh tới làm gì, có héo lánh tới nổi không? Hẳn nhiều người đã không khỏi ái ngại, đặt ra câu hỏi ấy khi thấy trên tờ Sài Gòn Tiếp Thị  mà như tên gọi của nó, là cầu nối giữa doanh nghiệp và người tiêu dùng, nơi tiếp cận thị trường của doanh nghiệp, nói cách khác là một thứ chợ - xuất hiện một chuyên mục gọi là Trò chuyện triết học. Với nhiều người, triết học là cái gì đó cao siêu, xa vời, chỉ có thể kính nhi viễn chi chứ chẳng ăn nhập gì với cuộc sống trần trụi, với thời buổi mà con người phải lặn hụp mệt nhoài trong cuộc mưu sinh, phải nhanh mắt nhanh tay chụp giựt cho mình những thứ có thể chụp giựt được, phải đối diện với bao nhiêu câu hỏi, bao nhiêu thách thức của cuộc sống trước mặt. Nói tóm lại, triết học là cái gì đó vô bổ, chỉ để dành cho những kẻ rỗi hơi.', 356.000, 0, 0, 1, 30, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(110, '8935081112491', 'Trò Chuyện Triết Học (Tập 2)', 'Triết học là một môn học không khó nhưng để nói chuyện về triết học cho mọi người nghe, mọi người cùng hiểu thì không phải dễ. Nhà nghiên cứu Bùi Văn Nam Sơn đã làm được điều ở vế thứ hai thông qua cuốn sách quý này. Đây không phải là cuốn sách nhập môn triết học và cũng không hẳn là cuốn sách về lịch sử triết học, tuy nhiên nó có đủ các yếu tố của 2 loại sách đó.', 196.000, 0, 0, 1, 30, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(111, '978604943352', 'Trò Chuyện Triết Học (Tập 3)', 'Trò Chuyện Triết Học tiếp tục tập hợp các bài viết từ nhiều thời điểm và cơ hội khác nhau. Có khi là lời giới thiệu hay lời bạt cho những quyển sách hay. Có khi là phần đóng góp nhỏ bé của tác giả cho các tập Kỷ yếu mừng các danh nhân văn hóa - khoa học trong nước và các sự kiện văn hóa lớn trên thế giới. Có khi là những bài báo, bài trả lời phỏng vấn... trong nhiều dịp khác nhau. Chúng được tập hợp và sắp xếp không theo trình tự thời gian hay một định hướng có trước nào cả. Để giữ được tính ngẫu hứng, đa dạng và... vui vẻ!', 413.000, 0, 0, 1, 30, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(112, '8935081112491', 'Trò chuyện triết học trọn bộ 9 tập', 'Những người đọc không chuyên có thể dễ dàng tìm hiểu về triết học, các khái niệm cơ bản của triết học, các trào lưu và các triết gia kinh điển thông qua những lời văn nhẹ nhàng, cô đọng và hóm hỉnh. Qua cuốn sách, bạn đọc có thể hiểu được triết học là gì và lợi ích của nó đối với cuộc sống thường nhật. Tuy nhiên, tác giả không định nghĩa nó rõ ràng như cách thông thường người ta hay làm, việc hiểu được khái niệm là sản phẩm của quá trình tư duy - biết - hiểu - cảm - ngộ mà tác giả là người thầy, người dẫn đường tin cậy và uyên thâm: cung cấp tư liệu, hướng dẫn phương pháp và giảng giải một cách dễ hiểu nhất. Không thể có triết học nếu không có sự tự giải phóng tâm hồn. Điều ấy đòi hỏi sự dũng cảm, kể cả sự dũng cảm dấn mình vào chỗ bấp bênh, nghi ngờ, thất vọng.', 317.000, 0, 0, 1, 30, 2, '2023-11-18 19:38:10', '2023-11-18 19:38:10'),
(113, '9781408855652', 'Harry Potter and the Philosopher\'s Stone', 'Harry Potter and the Philosopher\'s Stone is a fantasy novel written by British author J. K. Rowling. The first novel in the Harry Potter series and Rowling\'s debut novel, it follows Harry Potter, a young wizard who discovers his magical heritage on his eleventh birthday, when he receives a letter of acceptance to Hogwarts School of Witchcraft and Wizardry. Harry makes close friends and a few enemies during his first year at the school and with the help of his friends, Ron Weasley and Hermione Granger, he faces an attempted comeback by the dark wizard Lord Voldemort, who killed Harry\'s parents, but failed to kill Harry when he was just 15 months old.', 261.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(114, '9781338878936', 'Harry Potter and the Chamber of Secrets', 'The Chamber is flanked with towering pillars entwined with carved wood, and a tall statue of Salazar Slytherin is at the far end. The basilisk rests within the mouth of the statue, possibly symbolising Salazar Slytherin\'s ability to speak Parseltongue. Sometime before 1992, the chamber flooded with water.', 87.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(115, '9781338815283', 'Harry Potter and the Prisoner of Azkaban', 'Harry Potter and the Prisoner of Azkaban is a fantasy novel written by British author J. K. Rowling and is the third in the Harry Potter series. The book follows Harry Potter, a young wizard, in his third year at Hogwarts School of Witchcraft and Wizardry.', 131.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(116, '9780439139595', 'Harry Potter and the Goblet of Fire', 'The novels follow Harry Potter, an 11yearold boy who discovers he is the son of famous wizards and will attend Hogwarts School of Witchcraft and Wizardry. Harry learns of an entire society of wizards and witches. He befriends several Hogwarts students and teachers along his journey.', 488.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(117, '9780439358064', 'Harry Potter and the Order of the Phoenix', 'It follows Harry Potter\'s struggles through his fifth year at Hogwarts School of Witchcraft and Wizardry, including the surreptitious return of the antagonist Lord Voldemort, O.W.L. exams, and an obstructive Ministry of Magic.', 456.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(118, '9781408855652', 'Harry Potter and the HalfBlood Prince', 'owling, J. K. In this book, Harry Potter learns a lot about Lord Voldemort\'s past, and Harry Potter prepares for the final battle against his nemesis with the help of Headmaster Dumbledore. But in that time, Voldemort returns to power, and makes a plan to destroy Harry.', 140.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(119, '9780545010221', 'Harry Potter and the Deathly Hallows', 'The Deathly Hallows is about Harry Potter and his friends finding ways to destroy Voldemort. They learn that even good contains a bit of evil, and vise versa. Even though the trio faces many difficulties, they persevere.', 376.000, 0, 0, 1, 3, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(120, '9780545010221', 'The God Equation', 'The God Equation: The Quest for a Theory of Everything is a popular science book by the futurist and physicist Michio Kaku. The book was initially published on April 6, 2021, by Doubleday. The book debuted at number six on The New York Times nonfiction best-seller list for the week ending April 10, 2021.', 423.000, 0, 0, 1, 15, 4, '2023-11-18 19:52:41', '2023-11-18 19:52:41'),
(121, '9780141439556', 'Đồi gió hú', 'Giới thiệu về Đồi Gió Hú cuốn tiểu thuyết bắt nguồn từ trang viên Yorkshire trên cánh đồng hoang của câu chuyện. Câu chuyện tập trung vào tình yêu trọn vẹn, nồng nàn nhưng cuối cùng cũng phải chịu số phận giữa Catherine Earnshaw và Heathcliff, cũng như việc niềm đam mê không được giải quyết này cuối cùng đã hủy hoại họ và những người xung quanh như thế nào.', 184.000, 0, 0, 2, 1, 1, '2023-11-18 12:32:25', '2023-11-18 12:32:25');
INSERT INTO `books` (`id`, `isbn_13`, `title`, `book_description`, `price`, `sale_off`, `views`, `fm_id`, `au_id`, `pub_id`, `creation_date`, `updation_date`) VALUES
(122, '9780141439556', 'Wuthering Heights', 'Wuthering Heights is the first and only novel by the English author Emily Brontë, initially published in 1847 under her pen name Ellis Bell.', 245.000, 0, 0, 2, 1, 4, '2023-11-18 12:32:25', '2023-11-18 12:32:25'),
(123, '9780141439518', 'Kiêu hãnh và định kiến', 'Kiêu hãnh và định kiến là tác phẩm nổi tiếng nhất của nhà văn Anh Jane Austen. Tiểu thuyết được viết từ năm 1796 đến năm 1797 và xuất bản năm 1813. Câu chuyện nói về tình yêu và hôn nhân của tầng lớp quý tộc nhỏ tại Anh vào đầu thế kỷ 19.', 81.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03'),
(124, '9780141439518', 'Pride and Prejudice', 'Pride and Prejudice is an 1813 novel of manners by English author Jane Austen. The novel follows the character development of Elizabeth Bennet, the protagonist of the book, who learns about the repercussions of hasty judgments and comes to appreciate the difference between superficial goodness and actual goodness.', 247.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03'),
(125, '9780141439587', 'Emma', 'Jane Austen là một tiểu thuyết gia người Anh. Bà nổi tiếng với sáu cuốn tiểu thuyết lấy bối cảnh giới địa chủ trung lưu Anh vào cuối thế kỷ 18. Cốt truyện của Austen thường phản ánh tình cảnh phụ thuộc của người phụ nữ vào hôn nhân như là cứu cánh duy nhất để đảm bảo vị thế xã hội và lợi ích vật chất.', 238.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03'),
(126, '9780141439587', 'Emma', 'Emma is a novel written by English author Jane Austen. It is set in the fictional country village of Highbury and the surrounding estates of Hartfield, Randalls and Donwell Abbey, and involves the relationships among people from a small number of families.', 435.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(127, '9780141439662', 'Sense and Sensibility', 'Sense and Sensibility is the first novel by the English author Jane Austen, published in 1811. It was published anonymously; By A Lady appears on the title page where the author\'s name might have been. It tells the story of the Dashwood sisters, Elinor and Marianne as they come of age.', 88.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(128, '9798861522953', 'Thuyết phục', 'Thuyết phục là cuốn tiểu thuyết cuối cùng được hoàn thành bởi tác giả người Anh Jane Austen. Nó được xuất bản vào ngày 20 tháng 12 năm 1817, cùng với Tu viện Northanger, sáu tháng sau khi bà qua đời, mặc dù trang tiêu đề ghi ngày 1818.', 484.000, 0, 0, 2, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(129, '9798861522953', 'Persuasion: Novel', 'Persuasion is the last novel completed by the English author Jane Austen. It was published on 20 December 1817, along with Northanger Abbey, six months after her death, although the title page is dated 1818.', 183.000, 0, 0, 2, 2, 1, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(130, '9781408855652', 'Harry Potter và Hòn đá Phù thủy', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 475.000, 0, 0, 2, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(131, '9781338878936', 'Harry Potter và phòng chứa bí mật', 'Harry Potter và tên tù nhân ngục Azkaban là một quyển tiểu thuyết thuộc thể loại giả tưởng kỳ ảo được viết bởi văn sĩ người Anh J. K. Rowling, đây cũng là tập thứ 3 trong bộ truyện Harry Potter. Quyển sách theo chân Harry Potter, cậu phù thủy nhỏ, trong năm học thứ ba của mình tại Trường Phù thủy và Pháp sư Hogwarts.', 114.000, 0, 0, 2, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(132, '9781338815283', 'Harry Potter Và Tên Tù Nhân Ngục Azkaban', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 378.000, 0, 0, 2, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(133, '9780439139595', 'Harry Potter Và Chiếc Cốc Lửa', 'Harry Potter và chiếc cốc lửa là một quyển sách thuộc thể loại giả tưởng kỳ ảo được viết bởi tác giả người Anh J. K. Rowling và đây cũng là phần thứ tư trong bộ tiểu thuyết Harry Potter.', 109.000, 0, 0, 2, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(134, '9780439358064', 'Harry Potter Và Hội Phượng Hoàng', 'Harry Potter và hội Phượng hoàng là quyển thứ 5 trong bộ sách Harry Potter của nhà văn J. K. Rowling. Quyển này được đồng loạt xuất bản vào ngày 21 tháng 6 năm 2003 tại Anh, Hoa Kỳ, Canada, Úc và một vài quốc gia khác. Trong ngày đầu tiên xuất bản, nó đã bán được gần 7 triệu cuốn riêng tại Hoa Kỳ và Anh.', 173.000, 0, 0, 2, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(135, '9781408855652', 'Harry Potter Và Hoàng Tử Lai', 'Harry Potter và Hoàng tử lai là quyển sách thứ sáu trong bộ sách giả tưởng nổi tiếng Harry Potter của tác giả J.K. Rowling. Cũng như các quyển trước, nó cũng trở thành một trong những bestseller của năm nó xuất bản.', 221.000, 0, 0, 2, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(136, '9780545010221', 'Harry Potter Và Bảo Bối Tử Thần', 'Harry Potter và Bảo bối Tử thần là cuốn sách thứ bảy và cũng là cuối cùng của bộ tiểu thuyết giả tưởng Harry Potter của nhà văn Anh J.K. Rowling. Nguyên bản tiếng Anh được phát hành đồng thời tại Anh, Hoa Kỳ, Canada và một số nước khác vào ngày 21 tháng 7 năm 2007.', 415.000, 0, 0, 2, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(137, '9781408855652', 'Harry Potter and the Philosopher\'s Stone', 'Harry Potter and the Philosopher\'s Stone is a fantasy novel written by British author J. K. Rowling. The first novel in the Harry Potter series and Rowling\'s debut novel, it follows Harry Potter, a young wizard who discovers his magical heritage on his eleventh birthday, when he receives a letter of acceptance to Hogwarts School of Witchcraft and Wizardry. Harry makes close friends and a few enemies during his first year at the school and with the help of his friends, Ron Weasley and Hermione Granger, he faces an attempted comeback by the dark wizard Lord Voldemort, who killed Harry\'s parents, but failed to kill Harry when he was just 15 months old.', 143.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(138, '9781338878936', 'Harry Potter and the Chamber of Secrets', 'The Chamber is flanked with towering pillars entwined with carved wood, and a tall statue of Salazar Slytherin is at the far end. The basilisk rests within the mouth of the statue, possibly symbolising Salazar Slytherin\'s ability to speak Parseltongue. Sometime before 1992, the chamber flooded with water.', 433.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(139, '9781338815283', 'Harry Potter and the Prisoner of Azkaban', 'Harry Potter and the Prisoner of Azkaban is a fantasy novel written by British author J. K. Rowling and is the third in the Harry Potter series. The book follows Harry Potter, a young wizard, in his third year at Hogwarts School of Witchcraft and Wizardry.', 414.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(140, '9780439139595', 'Harry Potter and the Goblet of Fire', 'The novels follow Harry Potter, an 11yearold boy who discovers he is the son of famous wizards and will attend Hogwarts School of Witchcraft and Wizardry. Harry learns of an entire society of wizards and witches. He befriends several Hogwarts students and teachers along his journey.', 274.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(141, '9780439358064', 'Harry Potter and the Order of the Phoenix', 'It follows Harry Potter\'s struggles through his fifth year at Hogwarts School of Witchcraft and Wizardry, including the surreptitious return of the antagonist Lord Voldemort, O.W.L. exams, and an obstructive Ministry of Magic.', 198.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(142, '9781408855652', 'Harry Potter and the HalfBlood Prince', 'owling, J. K. In this book, Harry Potter learns a lot about Lord Voldemort\'s past, and Harry Potter prepares for the final battle against his nemesis with the help of Headmaster Dumbledore. But in that time, Voldemort returns to power, and makes a plan to destroy Harry.', 320.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(143, '9780545010221', 'Harry Potter and the Deathly Hallows', 'The Deathly Hallows is about Harry Potter and his friends finding ways to destroy Voldemort. They learn that even good contains a bit of evil, and vise versa. Even though the trio faces many difficulties, they persevere.', 291.000, 0, 0, 2, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(144, '9780143136132', 'Bà Dalloway', 'Bà Dalloway là một cuốn tiểu thuyết của Virginia Woolf xuất bản vào ngày 14 tháng 5 năm 1925. Nó kể chi tiết một ngày trong cuộc đời của Clarissa Dalloway, một phụ nữ thuộc tầng lớp thượng lưu hư cấu ở Anh sau Thế chiến thứ nhất. Đây là một trong những tiểu thuyết nổi tiếng nhất của Woolf.', 256.000, 0, 0, 2, 4, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(145, '9780143136132', 'Mrs. Dalloway', 'Dalloway, novel by Virginia Woolf published in 1925. It examines one day in the life of Clarissa Dalloway, an upperclass Londoner married to a member of Parliament. Mrs. Dalloway is essentially plotless; what action there is takes place mainly in the characters consciousness.', 412.000, 0, 0, 2, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(146, '9780143136132', 'Mrs. Dalloway', 'Dalloway, novel by Virginia Woolf published in 1925. It examines one day in the life of Clarissa Dalloway, an upperclass Londoner married to a member of Parliament. Mrs. Dalloway is essentially plotless; what action there is takes place mainly in the characters consciousness.', 389.000, 0, 0, 2, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(147, '9780143131847', 'Frankenstein', 'Frankenstein: hay The Modern Prometheus là một cuốn tiểu thuyết năm 1818 được viết bởi tác giả người Anh Mary Shelley. Frankenstein kể câu chuyện về Victor Frankenstein, một nhà khoa học trẻ, người đã tạo ra một sinh vật có trí tuệ trong một thí nghiệm khoa học không chính thống.', 457.000, 0, 0, 2, 4, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(148, '9780143131847', 'Frankenstein', 'The book tells the story of Victor Frankenstein, a Swiss student of natural science who creates an artificial man from pieces of corpses and brings his creature to life. Though it initially seeks affection, the monster inspires loathing in everyone who meets it.', 338.000, 0, 0, 2, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(149, '9789384225483', 'Ông già và biển cả', 'Ông già và Biển cả là một tiểu thuyết ngắn được Ernest Hemingway viết ở Cuba năm 1951 và xuất bản năm 1952. Nó là truyện ngắn dạng viễn tưởng cuối cùng được Hemingway viết và được xuất bản khi ông còn sống. Đây cũng là tác phẩm nổi tiếng và là một trong những tác phẩm đỉnh cao trong sự nghiệp sáng tác của nhà văn.', 121.000, 0, 0, 2, 6, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(150, '9789384225483', 'The Old Man and the Sea', 'The Old Man and the Sea is a 1952 novella written by the American author Ernest Hemingway. Written between December 1950 and February 1951, it tells the story of Santiago, an aging fisherman who catches a giant marlin after a long struggle, but then loses his bounty to sharks.', 274.000, 0, 0, 2, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(151, '9780684803357', 'Chuông nguyện hồn ai', 'Chuông nguyện hồn ai là tiểu thuyết được xuất bản năm 1940 của nhà văn Mỹ Ernest Miller Hemingway. Cuốn tiểu thuyết viết về Robert Jordan, một người Mỹ thuộc Lữ đoàn quốc tế, tham gia chống Phát xít trong cuộc nội chiến Tây Ban Nha.', 478.000, 0, 0, 2, 6, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(152, '9780684803357', 'For Whom the Bell Tolls', 'For Whom the Bell Tolls is a novel by Ernest Hemingway published in 1940. It tells the story of Robert Jordan, a young American volunteer attached to a Republican guerrilla unit during the Spanish Civil War. As a dynamiter, he is assigned to blow up a bridge during an attack on the city of Segovia.', 91.000, 0, 0, 2, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(153, '9780140447934', 'Chiến tranh và hòa bình', 'Chiến tranh và hòa bình là một tiểu thuyết của Lev Nikolayevich Tolstoy, được xuất bản rải rác trong giai đoạn 1865–1869. Tác phẩm được xem là thành tựu văn học xuất sắc nhất của Tolstoy, cũng như một tác phẩm kinh điển của văn học thế giới.', 238.000, 0, 0, 2, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(154, '9780140447934', 'War and Peace', 'War and Peace is a literary work by Russian author Leo Tolstoy. Set during the Napoleonic Wars, the work mixes fictional narrative with chapters discussing history and philosophy. First published serially beginning in 1865, the work was rewritten and published in its entirety in 1869.', 130.000, 0, 0, 2, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(155, '9780684803357', 'Anna Karenina', 'Anna Karenina là một tiểu thuyết của nhà văn Nga Lev Nikolayevich Tolstoy, được đăng tải nhiều kỳ trên tờ báo Ruskii Vestnik từ năm 1873 đến năm 1877 trước khi xuất bản thành ấn phẩm hoàn chỉnh. Anna Karenina được xem như là một đỉnh cao của tiểu thuyết hiện thực.', 404.000, 0, 0, 2, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(156, '9780684803357', 'Anna Karenina', 'Anna Karenina is a novel by the Russian author Leo Tolstoy, first published in book form in 1878. Considered to be one of the greatest works of literature ever written, Tolstoy himself called it his first true novel.', 366.000, 0, 0, 2, 7, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(157, '9780553210354', 'Cái chết của Ivan Ilyich', 'Cái chết của Ivan Ilyich, xuất bản lần đầu năm 1886, là một tiểu thuyết của Leo Tolstoy, được coi là một trong những kiệt tác của tiểu thuyết cuối cùng của ông, được viết ngay sau khi ông cải đạo vào cuối những năm 1870.', 105.000, 0, 0, 2, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(158, '9780553210354', 'The Death of Ivan Ilyich', 'The Death of Ivan Ilyich, first published in 1886, is a novella by Leo Tolstoy, considered one of the masterpieces of his late fiction, written shortly after his religious conversion of the late 1870s.', 161.000, 0, 0, 2, 7, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(159, '9780486415871', 'Tội ác và hình phạt', 'Tội ác và hình phạt là một tiểu thuyết của nhà văn Nga Fyodor Mikhailovich Dostoyevsky. Tiểu thuyết này cùng với Anh em nhà Karamazov là hai tác phẩm nổi tiếng nhất của Dostoevsky. Tạp chí Times đã bình chọn Tội ác và hình phạt là một trong số những cuốn sách vĩ đại nhất mọi thời đại.', 344.000, 0, 0, 2, 8, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(160, '9780486415871', 'Crime and Punishment', 'Crime and Punishment is a novel by the Russian author Fyodor Dostoevsky. It was first published in the literary journal The Russian Messenger in twelve monthly installments during 1866. It was later published in a single volume.', 240.000, 0, 0, 2, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(161, '9780374528379', 'Anh em nhà Karamazov', 'Anh em nhà Karamazov là tác phẩm cuối cùng của Fyodor Dostoevsky. Fyodor Dostoevsky thuộc loại thiên tài hiếm hoi mà càng về cuối đời thì sự nghiệp sáng tác càng lên tới đỉnh cao hơn. Anh em nhà Karamazov là tác phẩm vĩ đại nhất của ông.', 446.000, 0, 0, 2, 8, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(162, '9780374528379', 'The Brothers Karamazov', 'The Brothers Karamazov, also translated as The Karamazov Brothers, is the last novel by Russian author Fyodor Dostoevsky. Dostoevsky spent nearly two years writing The Brothers Karamazov, which was published as a serial in The Russian Messenger from January 1879 to November 1880.', 219.000, 0, 0, 2, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(163, '9780679734512', 'Ác quỷ', 'Ác quỷ là một cuốn tiểu thuyết của Fyodor Dostoevsky, được xuất bản lần đầu trên tạp chí The Russian Messenger vào năm 1871–72. Nó được coi là một trong bốn kiệt tác được Dostoevsky viết sau khi ông trở về từ cuộc lưu đày ở Siberia, cùng với Tội ác và Trừng phạt, Thằng ngốc và Anh em nhà Karamazov.', 154.000, 0, 0, 2, 8, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(164, '9780679734512', 'Demons (Dostoevsky novel)', 'Demons is a novel by Fyodor Dostoevsky, first published in the journal The Russian Messenger in 1871–72. It is considered one of the four masterworks written by Dostoevsky after his return from Siberian exile, along with Crime and Punishment, The Idiot, and The Brothers Karamazov.', 116.000, 0, 0, 2, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(165, '9781503215672', 'Những cuộc phiêu lưu của Tom Sawyer', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 96.000, 0, 0, 2, 9, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(166, '9781503215672', 'The Adventures of Tom Sawyer', 'The Adventures of Tom Sawyer is an 1876 novel by Mark Twain about a boy growing up along the Mississippi River. It is set in the 1840s in the town of St. Petersburg, which is based on Hannibal, Missouri, where Twain lived as a boy.', 169.000, 0, 0, 2, 9, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(167, '9780143107323', 'Những cuộc phiêu lưu của Huckleberry Finn', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 236.000, 0, 0, 2, 9, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(168, '9780143107323', 'Adventures of Huckleberry Finn', 'Adventures of Huckleberry Finn is a novel by American author Mark Twain, which was first published in the United Kingdom in December 1884 and in the United States in February 1885.', 186.000, 0, 0, 2, 9, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(169, '9780679722762', 'Ulysses', 'Ulysses là tiểu thuyết gồm 3 phần của James Joyce xuất bản lần đầu năm 1922. Với ẩn dụ về sử thi Odysseus, thủ pháp dòng ý thức và nội dung được nén chặt bằng những sự kiện miên man trôi theo suy tưởng của các nhân vật diễn ra chỉ trong một ngày 16 tháng 6 năm 1904, tác phẩm được tạp chí Time đánh giá là một trong số những kiệt tác văn chương lớn nhất thế giới thế kỷ 20.', 463.000, 0, 0, 2, 10, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(170, '9780679722762', 'Ulysses', 'Ulysses is a modernist novel by Irish writer James Joyce. Parts of it were first serialized in the American journal The Little Review from March 1918 to December 1920, and the entire work was published in Paris by Sylvia Beach on 2 February 1922, Joyce\'s fortieth birthday.', 161.000, 0, 0, 2, 10, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(171, '9780140186475', 'Người Dublin', 'Dubliners là một tuyển tập gồm mười lăm truyện ngắn của James Joyce, được xuất bản lần đầu tiên vào năm 1914. Cuốn sách này mô tả một cách tự nhiên về cuộc sống của tầng lớp trung lưu Ireland trong và xung quanh Dublin trong những năm đầu của thế kỷ 20.', 178.000, 0, 0, 2, 10, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(172, '9780140186475', 'Dubliners', 'Dubliners is a collection of fifteen short stories by James Joyce, first published in 1914. It presents a naturalistic depiction of Irish middle class life in and around Dublin in the early years of the 20th century.', 452.000, 0, 0, 2, 10, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(173, '9780316024976', 'Những kẻ xuất chúng', 'Những kẻ ngoài lề: Câu chuyện thành công là cuốn sách phi hư cấu do Malcolm Gladwell viết và được Little, Brown and Company xuất bản vào ngày 18 tháng 11 năm 2008. Trong Outlier, Gladwell xem xét các yếu tố góp phần vào mức độ thành công cao.', 408.000, 0, 0, 2, 11, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(174, '9780141036250', 'Outliers: The Story of Success', 'Outliers: The Story of Success is the nonfiction book written by Malcolm Gladwell and published by Little, Brown and Company on November 18, 2008. In Outliers, Gladwell examines the factors that contribute to high levels of success.', 129.000, 0, 0, 2, 11, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(175, '9780316346627', 'Điểm bùng phát', 'Điểm tới hạn: Những điều nhỏ bé có thể tạo nên sự khác biệt lớn như thế nào là cuốn sách đầu tay của Malcolm Gladwell, được Little, Brown xuất bản lần đầu vào năm 2000. Gladwell định nghĩa điểm tới hạn là thời điểm của khối lượng tới hạn, ngưỡng, điểm sôi.', 77.000, 0, 0, 2, 11, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(176, '9780316346627', 'The Tipping Point: How Little Things Can Make a Big Difference', 'The Tipping Point: How Little Things Can Make a Big Difference is the debut book by Malcolm Gladwell, first published by Little, Brown in 2000. Gladwell defines a tipping point as the moment of critical mass, the threshold, the boiling point.', 208.000, 0, 0, 2, 11, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(177, '9788483468913', 'Lý thuyết về tất cả mọi thứ', 'Trong tác phẩm xuất sắc của nhà vật lý Stephen Hawking, Lược sử thời gian, ông đã trình bày cho chúng ta một cái nhìn mới táo bạo về vũ trụ của chúng ta, nó bắt đầu như thế nào và quan điểm cũ của chúng ta về vật lý cũng như các lý thuyết mệt mỏi về sự hình thành vũ trụ không còn phù hợp như thế nào nữa.', 78.000, 0, 0, 2, 12, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(178, '9788817079761', 'The illustrated theory of everything: The origin and fate of the universe', 'In physicist Stephen Hawking\'s brilliant opus, A Brief History of Time, he presented us with a bold new look at our universe, how it began, and how our old views of physics and tired theories about the creation of the universe were no longer relevant.', 178.000, 0, 0, 2, 12, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(179, '9780553380163', 'Lược sử thời gian', 'Điểm tới hạn: Những điều nhỏ bé có thể tạo nên sự khác biệt lớn như thế nào là cuốn sách đầu tay của Malcolm Gladwell, được Little, Brown xuất bản lần đầu vào năm 2000. Gladwell định nghĩa điểm tới hạn là thời điểm của khối lượng tới hạn, ngưỡng, điểm sôi.', 349.000, 0, 0, 2, 12, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(180, '9780553380163', 'A Brief History of Time', 'A Brief History of Time: From the Big Bang to Black Holes is a book on theoretical cosmology by English physicist Stephen Hawking. It was first published in 1988. Hawking wrote the book for readers who had no prior knowledge of physics.', 83.000, 0, 0, 2, 12, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(181, '9782070257539', 'Buồn nôn', 'Buồn nôn là một tiểu thuyết triết học của triết gia hiện sinh Jean-Paul Sartre, xuất bản năm 1938. Đây là tiểu thuyết đầu tay của Sartre. Cuốn tiểu thuyết diễn ra ở Bouville, một thị trấn tương tự như Le Havre.', 309.000, 0, 0, 2, 13, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(182, '9782070257539', 'Nausea', 'Nausea is a philosophical novel by the existentialist philosopher Jean-Paul Sartre, published in 1938. It is Sartre\'s first novel. The novel takes place in \'Bouville\' a town similar to Le Havre.', 433.000, 0, 0, 2, 13, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(183, '9788532666826', 'Tồn tại và hư vô', 'L\'Être et le Néant là một tác phẩm lớn, hơn 700 trang sách, của Jean-Paul Sartre, một trong những nhà triết học hàng đầu của Pháp thế kỷ 20, một trong những người sáng lập ra Chủ nghĩa hiện sinh. Lúc L\'Être et le Néant ra đời, chẳng mấy người hiểu nó.', 472.000, 0, 0, 2, 13, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(184, '9789754680096', 'Existentialism Is a Humanism', 'Existentialism Is a Humanism is a 1946 work by the philosopher Jean-Paul Sartre, based on a lecture by the same name he gave at Club Maintenant in Paris, on 29 October 1945.', 398.000, 0, 0, 2, 13, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(185, '9780241989692', 'Những Con Số Biết Nói  71 Câu Chuyện Phơi Bày Sự Thật Về Thế Giới Chúng Ta Đang Sống', 'Chứa đựng những thông tin hấp dẫn và những ví dụ đáng nhớ, Numbers Don\'t Lie tiết lộ cách Hoa Kỳ đang dẫn đầu xu hướng tiêu thụ thịt gà ngày càng tăng trên toàn thế giới, việc tiêm chủng mang lại lợi tức đầu tư tốt nhất và tại sao ô tô điện không tuyệt vời như chúng ta nghĩ.', 84.000, 0, 0, 2, 14, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(186, '9780241989692', 'Numbers Don\'t Lie: 71 Stories to Help Us Understand the Modern World', 'An essential guide to understanding how numbers reveal the true state of our world–exploring a wide range of topics including energy, the environment, technology, transportation, and food production. Vaclav Smil\'s mission is to make facts matter.', 112.000, 0, 0, 2, 14, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(187, '9780593297063', 'How the World Really Works: The Science Behind How We Got Here and Where We\'re Going', 'In How the World Really Works: A Scientist\'s Guide to Our Past, Present and Future, Vaclav Smil explores seven fundamental areas that govern human survival and prosperity, covering topics such as food production, energy and globalisation.', 480.000, 0, 0, 2, 14, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(188, '9780307473332', 'Phương Trình Của Chúa - Cuộc Truy Tìm Lý Thuyết Của Vạn Vật', 'Một cuốn sách xuất sắc được viết bởi một nhà truyền thông khoa học bậc thầy. Nếu ai đó có thể làm sáng tỏ những điều thâm thúy của toán học và lý thuyết dây, thì đó chính là Michio Kaku. Trong cuốn sách nhỏ tuyệt vời này, tác giả giải thích bằng những thuật ngữ rõ ràng và đơn giản những đột phá về khái niệm và những câu hỏi chưa được giải đáp - trong việc tìm kiếm một lý thuyết thống nhất vĩ đại của vạn vật.', 212.000, 0, 0, 2, 15, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(189, '9780307473332', 'Tương Lai Nhân Loại', 'Địa cầu hóa Sao Hỏa, viếng thăm các vì sao, sự bất tử và định mệnh bên ngoài Trái Đất\n\n“Theo tôi, nếu không tiến ra không gian, nhân loại sẽ không có tương lai.” –Stephen Hawking\n\nVậy hành trình tiến ra không gian ấy sẽ thuận lợi hay khó khăn? Sẽ cần bao nhiêu thời gian và những công nghệ gì? Nhân loại đã chuẩn bị những gì cho định mệnh đó? Và cuộc sống con người khi ấy sẽ ra sao?', 166.000, 0, 0, 2, 15, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(190, '9780307473332', 'Physics of the Future', 'Physics of the Future: How Science Will Shape Human Destiny and Our Daily Lives by the Year 2100 is a 2011 book by theoretical physicist Michio Kaku, author of Hyperspace and Physics of the Impossible. In it Kaku speculates about possible future technological development over the next 100 years.', 95.000, 0, 0, 2, 15, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(191, '9780307473332', 'The Communist Manifesto', 'The Communist Manifesto, originally the Manifesto of the Communist Party, is a political pamphlet written by Karl Marx and Friedrich Engels, commissioned by the Communist League and originally published in London in 1848.', 295.000, 0, 0, 2, 15, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(192, '9780717802418', 'Capital, Volume I', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 243.000, 0, 0, 2, 16, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(193, '9780486477480', 'Capital, Volume II', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 145.000, 0, 0, 2, 16, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(194, '9780853150275', 'Capital, Volume III', 'Capital. A Critique of Political Economy. Volume I: The Process of Production of Capital is the first of three treatises that make up Das Kapital, a critique of political economy by the German philosopher and economist Karl Marx.', 86.000, 0, 0, 2, 16, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(195, '9780679720201', 'Người xa lạ', 'Người xa lạ là một tiểu thuyết của Albert Camus được viết vào năm 1942. Đây là một tác phẩm lạ thường nói về một người đàn ông bình thường, người mà cuối cùng đã bị tống giam vì tội giết người, và ngồi chờ bị hành hình.', 436.000, 0, 0, 2, 17, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(196, '9780679720201', 'The Stranger', 'Người xa lạ là một tiểu thuyết của Albert Camus được viết vào năm 1942. Đây là một tác phẩm lạ thường nói về một người đàn ông bình thường, người mà cuối cùng đã bị tống giam vì tội giết người, và ngồi chờ bị hành hình.', 454.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(197, '9780525564454', 'Thần thoại Sisyphus', 'Huyền thoại về Sisyphus là một tiểu luận triết học năm 1942 của Albert Camus. Chịu ảnh hưởng của các triết gia như Søren Kierkegaard, Arthur Schopenhauer và Friedrich Nietzsche, Camus giới thiệu triết lý phi lý của mình.', 436.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(198, '9780679720218', 'Dịch hạch', 'Dịch hạch là một cuốn tiểu thuyết nổi tiếng của Albert Camus xuất bản năm 1947. Một vài nhân vật của Dịch hạch đã xuất hiện trong cuốn sổ tay của Camus viết ở Alger năm 1938. Nhưng hơn cả là ở Oran, Algérie từ cuối năm 1940 đến mùa xuân năm 1942, rồi ở Pháp, Camus đã soạn thảo lại cuốn tiểu thuyết của mình.', 318.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(199, '9780679720201', 'The Stranger', 'The Stranger, also published in English as The Outsider, is a 1942 novella written by French author Albert Camus. The first of Camus\' novels published in his lifetime, the story follows Meursault, an indifferent settler in French Algeria, who, weeks after his mother\'s funeral, kills an unnamed Arab man in Algiers.', 314.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(200, '9780525564454', 'The Myth of Sisyphus', 'The Myth of Sisyphus is a 1942 philosophical essay by Albert Camus. Influenced by philosophers such as Søren Kierkegaard, Arthur Schopenhauer, and Friedrich Nietzsche, Camus introduces his philosophy of the absurd.', 161.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(201, '9780679720218', 'The Plague', 'The Plague is a 1947 absurdist novel by Albert Camus. It tells the story from the point of view of a narrator in the midst of a plague sweeping the French Algerian city of Oran. The narrator remains unknown until the start of the last chapter.', 419.000, 0, 0, 2, 17, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(202, '9780307277787', 'Giới tính thứ hai', 'Giới tính hạng hai là cuốn sách của nhà triết học hiện sinh người Pháp Simone de Beauvoir, xuất bản năm 1949. Trong cuốn sách, tác giả thảo luận về cách thức phụ nữ bị đối xử trong suốt chiều dài lịch sử. Beauvoir nghiên cứu và viết cuốn sách trong khoảng 14 tháng, từ năm 1946 đến năm 1949.', 84.000, 0, 0, 2, 18, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(203, '9780307277787', 'The Second Sex', 'The Second Sex is a 1949 book by the French existentialist philosopher Simone de Beauvoir, in which the author discusses the treatment of women in the present society as well as throughout all of history. Beauvoir researched and wrote the book in about 14 months between 1946 and 1949.', 479.000, 0, 0, 2, 18, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(204, '9780143039884', 'Eichmann ở Jerusalem', 'Eichmann ở Jerusalem: Một phóng sự về Sự Tầm Thường của Cái Ác là cuốn sách xuất bản năm 1963 của nhà lý luận chính trị Hannah Arendt.', 363.000, 0, 0, 2, 19, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(205, '9780143039884', 'Eichmann in Jerusalem', 'Eichmann in Jerusalem: A Report on the Banality of Evil is a 1963 book by political thinker Hannah Arendt. Arendt, a Jew who fled Germany during Adolf Hitler\'s rise to power, reported on the trial of Adolf Eichmann, one of the major organizers of the Holocaust, for The New Yorker.', 356.000, 0, 0, 2, 19, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(206, '9780156701532', 'The Origins of Totalitarianism', 'The Origins of Totalitarianism, published in 1951, was Hannah Arendt\'s first major work, wherein she describes and analyzes Nazism and Stalinism as the major totalitarian political movements of the first half of the 20th century.', 478.000, 0, 0, 2, 19, 5, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(207, '9788087888957', 'On Violence', 'An analysis of the nature, causes, and significance of violence in the second half of the twentieth century. Arendt also reexamines the relationship between war, politics, violence, and power.', 124.000, 0, 0, 2, 19, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(208, '9780618249060', 'Mùa xuân vắng lặng', 'Mùa xuân vắng lặng là một cuốn sách về đề tài khoa học môi trường của Rachel Carson. Tác phẩm được xuất bản vào ngày 27 tháng 9 năm 1962 và ghi lại những tác động xấu đến môi trường do sử dụng bừa bãi thuốc bảo vệ thực vật.', 204.000, 0, 0, 2, 20, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(209, '9780618249060', 'Silent Spring', 'Silent Spring is an environmental science book by Rachel Carson. Published on September 27, 1962, the book documented the environmental harm caused by the indiscriminate use of pesticides.', 289.000, 0, 0, 2, 20, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(210, '9780067575208', 'The Sense of Wonder', 'The Sense of Wonder relates Carson\'s intimate account of adventures with her young nephew, Roger, as they enjoy walks along the rocky coast of Maine and through dense forests and open fields, observing wildlife, strange plants, moonlight, and storm clouds and listening to the living music of insects in the underbrush.', 81.000, 0, 0, 2, 20, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(211, '9786041004757', 'Cho tôi xin một vé đi tuổi thơ', 'Cho tôi xin một vé đi tuổi thơ là truyện ngắn của nhà văn Nguyễn Nhật Ánh. Tác phẩm là một trong những sáng tác thành công nhất của ông và nhận được Giải thưởng Văn học ASEAN của năm 2010. Nguyễn Nhật Ánh viết ở mặt sau cuốn sách. Tôi viết cuốn sách này không dành cho trẻ em. Tôi viết cho những ai từng là trẻ em.', 170.000, 0, 0, 2, 21, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(212, '9786041004757', 'Mắt biếc', 'Mắt biếc là tiểu thuyết của nhà văn Nguyễn Nhật Ánh trong loạt truyện viết về tình yêu thanh thiếu niên của tác giả này cùng với Thằng quỷ nhỏ, Cô gái đến từ hôm qua,... Đây được xem là một trong những tác phẩm tiêu biểu của Nguyễn Nhật Ánh.', 359.000, 0, 0, 2, 21, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(213, '9786041000698', 'Tôi thấy hoa vàng trên cỏ xanh', 'Tôi thấy hoa vàng trên cỏ xanh là một tiểu thuyết dành cho thanh thiếu niên của nhà văn Nguyễn Nhật Ánh, xuất bản lần đầu tại Việt Nam vào ngày 9 tháng 12 năm 2010 bởi Nhà xuất bản Trẻ với phần tranh minh họa do Đỗ Hoàng Tường thực hiện.', 290.000, 0, 0, 2, 21, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(214, '9786041004689', 'Cánh đồng bất tận', 'Cánh đồng bất tận là tên một tập truyện ngắn phát hành năm 2005 của Nguyễn Ngọc Tư, đồng thời cũng là tên một truyện trong tập truyện ngắn đó được đăng báo lần đầu cùng năm.', 158.000, 0, 0, 2, 22, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(215, '9786041005167', 'Ngọn đèn không tắt', 'Cuốn sách “Ngọn Đèn Không Tắt” của tác giả Nguyễn Ngọc Tư là một tập truyện ngắn đầy hấp dẫn và thuyết phục về thanh niên hôm nay trên mọi mặt đời sống. Tác phẩm được viết với giọng văn mộc mạc, bình dị và ngôn ngữ đời thường, tạo nên một không khí tự nhiên về màu sắc và hương vị của mảnh đất cuối cùng của tổ quốc.', 445.000, 0, 0, 2, 22, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(216, '9786043943078', 'Hai Đứa Trẻ', 'Cuốn sách tập hợp những truyện ngắn đặc sắc của nhà văn Thạch Lam: Nắng trong vườn, Gió lạnh đầu mùa, Tiếng chim kêu, Đứa con đầu lòng, Bắt đầu, Nhà mẹ Lê, Một cơn giận, Hai đứa trẻ, Đứa con, Trong bóng tối buổi chiều, Cuốn sách bỏ quên, Dưới bóng hoàng lan,...', 338.000, 0, 0, 2, 23, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(217, '9786042198097', 'Hà Nội Băm Sáu Phố Phường', 'Văn học Việt Nam thời xưa có nhiều tác phẩm có giá trị to lớn về mặt nhân văn và nghệ thuật, đã được công nhận và chứng thực qua thời gian. Bộ sách Việt Nam danh tác bao gồm loạt tác phẩm đi cùng năm tháng như: Số đỏ (Vũ Trọng Phụng), Việc làng (Ngô Tất Tố), Gió đầu mùa, Hà Nội băm sáu phố phường (Thạch Lam), Miếng ngon Hà Nội (Vũ Bằng), Vang bóng một thời (Nguyễn Tuân). Hy vọng bộ sách sau khi tái bản sẽ giúp đông đảo tầng lớp độc giả thêm hiểu, tự hào và nâng niu kho tàng văn học nước nhà.', 88.000, 0, 0, 2, 23, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(218, '9780472068043', 'Số Đỏ', 'Văn học Việt Nam thời xưa có nhiều tác phẩm có giá trị to lớn về mặt nhân văn và nghệ thuật, đã được công nhận và chứng thực qua thời gian. Bộ sách Việt Nam danh tác bao gồm loạt tác phẩm đi cùng năm tháng như: Số đỏ (Vũ Trọng Phụng), Việc làng (Ngô Tất Tố), Gió đầu mùa, Hà Nội băm sáu phố phường (Thạch Lam), Miếng ngon Hà Nội (Vũ Bằng), Vang bóng một thời (Nguyễn Tuân). Hy vọng bộ sách sau khi tái bản sẽ giúp đông đảo tầng lớp độc giả thêm hiểu, tự hào và nâng niu kho tàng văn học nước nhà.', 315.000, 0, 0, 2, 24, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(219, '9786049638428', 'Làm Đĩ', 'Làm đĩ là một cuốn tiểu thuyết của nhà văn Việt Nam Vũ Trọng Phụng, tác phẩm được viết vào năm 1936 và xuất bản vào năm 1937. Đây được coi là cuốn tiểu thuyết đầu tiên của Việt Nam đề cập đến vấn đề mại dâm.', 465.000, 0, 0, 2, 24, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(220, '9786045326121', 'Chí Phèo', 'Chí Phèo là một truyện ngắn nổi tiếng của nhà văn Nam Cao viết vào tháng 2 năm 1941. Chí Phèo là một tác phẩm xuất sắc, thể hiện nghệ thuật viết truyện độc đáo của Nam Cao, đồng thời là một tấn bi kịch của một người nông dân nghèo bị tha hóa trong xã hội. Chí Phèo cũng là tên nhân vật chính của truyện.', 435.000, 0, 0, 2, 25, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(221, '9786043724943', 'Lão Hạc', 'Lão Hạc là một truyện ngắn của nhà văn Nam Cao được viết năm 1943. Tác phẩm được đánh giá là một trong những truyện ngắn khá tiêu biểu của dòng văn học hiện thực, nội dung truyện đã phần nào phản ánh được hiện trạng xã hội Việt Nam trong giai đoạn trước Cách mạng tháng Tám.', 187.000, 0, 0, 2, 25, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(222, '9786045342411', 'Bức xúc không làm ta vô can', 'Bức xúc không làm ta vô can chính là cuốn sách đầu tiên về thể loại chính luận mà tác giả luôn theo đuổi, một tác phẩm mang tinh phần phản biện xã hội sâu sắc. Sách được xuất bản năm 2015, là tập hợp những bài bình về các vấn đề trong xã hội, các vấn đề về văn hóa, những hiện tượng xã hội đương thời.', 301.000, 0, 0, 2, 26, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(223, '9786045342411', 'Điểm đến của cuộc đời: đồng hành với người cận tử và những bài học cho cuộc sống', 'Điểm đến của cuộc đời kể lại một hành trình không thể nào quên cùng những người cận tử. Dấn thân vào một thế giới của những bi kịch và tổn thất khổng lồ, của phẩm giá và lòng tự trọng trong hoàn cảnh khắc nghiệt, của sự phản bội và sợ hãi, của tình yêu mãnh liệt và hy vọng khôn nguôi, tóm lại, của tất cả những gì thuộc về con người, ở mức độ dữ dội nhất, tác giả muốn đi tìm câu trả lời cho thôi thúc nội tâm: ta nên ứng xử thế nào trước cái chết, và sự chết có thể dạy ta điều gì cho cuộc sống?', 260.000, 0, 0, 2, 26, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(224, '9786042262279', 'Dế Mèn phiêu lưu ký', 'Dế Mèn phiêu lưu ký là tác phẩm văn xuôi đặc sắc và nổi tiếng nhất của nhà văn Tô Hoài viết về loài vật, dành cho lứa tuổi thiếu nhi. Ban đầu truyện có tên là Con dế mèn do Nhà xuất bản Tân Dân, Hà Nội phát hành năm 1941. Sau đó, được sự ủng hộ nhiệt tình của nhân dân, Tô Hoài viết thêm truyện Dế Mèn phiêu lưu ký.', 303.000, 0, 0, 2, 27, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(225, '8935212327985', 'Tắt đèn', 'Tắt đèn là một trong những tác phẩm văn học tiêu biểu nhất của nhà văn Ngô Tất Tố. Đây là một tác phẩm văn học hiện thực phê phán với nội dung nói về cuộc sống khốn khổ của tầng lớp nông dân Việt Nam đầu thế kỉ XX dưới ách đô hộ của thực dân Pháp.', 418.000, 0, 0, 2, 28, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(226, '9786043943283', 'Lều chõng', 'Lều chõng là một tiểu thuyết phóng sự của nhà văn Ngô Tất Tố, ra mắt độc giả lần đầu ngày 10 tháng 3 năm 1939 tại Hà Nội.', 168.000, 0, 0, 2, 28, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(227, '8936067604634', 'Truyện Kiều', 'Đoạn trường tân thanh, thường được biết đến với cái tên đơn giản là Truyện Kiều, là một truyện thơ của đại thi hào Nguyễn Du. Đây được xem là truyện thơ nổi tiếng nhất và xét vào hàng kinh điển trong văn học Việt Nam, tác phẩm được viết bằng chữ Nôm, theo thể lục bát, gồm 3.254 câu.', 330.000, 0, 0, 2, 29, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(228, '8935081111609', 'Hiện Tượng Học Tinh Thần', 'Có thể khẳng định một cách chắc chắn rằng Hiện tượng học Tinh thần là một trong các tác phẩm khó nhất và tham vọng nhất trong kho tàng triết văn thế giới. Tuy thế, bất chấp nhận định của W. Winderlband hơn 100 năm trước “Giống người đã có thể hiểu được quyển Hiện tượng học Tinh thần của Hegel đang trên đà tuyệt chủng. Giờ đây, ngay số người đã chịu khó đọc tác phẩm ấy từ đầu đến cuối chắc cũng chỉ còn đếm được trên đầu ngón tay mà thôi” thì Hiện tượng học Tinh thần vẫn cứ tiếp tục có ma lực dị thường. Và muốn tìm hiểu về Hegel không còn cách nào khác chúng ta phải bắt đầu với Hiện tượng học Tinh thần – tác phẩm lớn đầu tiên và cũng là tác phẩm thiên tài nhất của ông.', 218.000, 0, 0, 2, 30, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(229, '8935081112804', 'Trò Chuyện Triết Học Tập 1', 'Ở nơi chợ búa ồn ã bán mua, nơi chợ đời bụi bặm, triết học héo lánh tới làm gì, có héo lánh tới nổi không? Hẳn nhiều người đã không khỏi ái ngại, đặt ra câu hỏi ấy khi thấy trên tờ Sài Gòn Tiếp Thị  mà như tên gọi của nó, là cầu nối giữa doanh nghiệp và người tiêu dùng, nơi tiếp cận thị trường của doanh nghiệp, nói cách khác là một thứ chợ - xuất hiện một chuyên mục gọi là Trò chuyện triết học. Với nhiều người, triết học là cái gì đó cao siêu, xa vời, chỉ có thể kính nhi viễn chi chứ chẳng ăn nhập gì với cuộc sống trần trụi, với thời buổi mà con người phải lặn hụp mệt nhoài trong cuộc mưu sinh, phải nhanh mắt nhanh tay chụp giựt cho mình những thứ có thể chụp giựt được, phải đối diện với bao nhiêu câu hỏi, bao nhiêu thách thức của cuộc sống trước mặt. Nói tóm lại, triết học là cái gì đó vô bổ, chỉ để dành cho những kẻ rỗi hơi.', 96.000, 0, 0, 2, 30, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(230, '8935081112491', 'Trò Chuyện Triết Học (Tập 2)', 'Triết học là một môn học không khó nhưng để nói chuyện về triết học cho mọi người nghe, mọi người cùng hiểu thì không phải dễ. Nhà nghiên cứu Bùi Văn Nam Sơn đã làm được điều ở vế thứ hai thông qua cuốn sách quý này. Đây không phải là cuốn sách nhập môn triết học và cũng không hẳn là cuốn sách về lịch sử triết học, tuy nhiên nó có đủ các yếu tố của 2 loại sách đó.', 286.000, 0, 0, 2, 30, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(231, '978604943352', 'Trò Chuyện Triết Học (Tập 3)', 'Trò Chuyện Triết Học tiếp tục tập hợp các bài viết từ nhiều thời điểm và cơ hội khác nhau. Có khi là lời giới thiệu hay lời bạt cho những quyển sách hay. Có khi là phần đóng góp nhỏ bé của tác giả cho các tập Kỷ yếu mừng các danh nhân văn hóa - khoa học trong nước và các sự kiện văn hóa lớn trên thế giới. Có khi là những bài báo, bài trả lời phỏng vấn... trong nhiều dịp khác nhau. Chúng được tập hợp và sắp xếp không theo trình tự thời gian hay một định hướng có trước nào cả. Để giữ được tính ngẫu hứng, đa dạng và... vui vẻ!', 172.000, 0, 0, 2, 30, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(232, '8935081112491', 'Trò chuyện triết học trọn bộ 9 tập', 'Những người đọc không chuyên có thể dễ dàng tìm hiểu về triết học, các khái niệm cơ bản của triết học, các trào lưu và các triết gia kinh điển thông qua những lời văn nhẹ nhàng, cô đọng và hóm hỉnh. Qua cuốn sách, bạn đọc có thể hiểu được triết học là gì và lợi ích của nó đối với cuộc sống thường nhật. Tuy nhiên, tác giả không định nghĩa nó rõ ràng như cách thông thường người ta hay làm, việc hiểu được khái niệm là sản phẩm của quá trình tư duy - biết - hiểu - cảm - ngộ mà tác giả là người thầy, người dẫn đường tin cậy và uyên thâm: cung cấp tư liệu, hướng dẫn phương pháp và giảng giải một cách dễ hiểu nhất. Không thể có triết học nếu không có sự tự giải phóng tâm hồn. Điều ấy đòi hỏi sự dũng cảm, kể cả sự dũng cảm dấn mình vào chỗ bấp bênh, nghi ngờ, thất vọng.', 234.000, 0, 0, 2, 30, 2, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(233, '9781408855652', 'Harry Potter and the Philosopher\'s Stone', 'Harry Potter and the Philosopher\'s Stone is a fantasy novel written by British author J. K. Rowling. The first novel in the Harry Potter series and Rowling\'s debut novel, it follows Harry Potter, a young wizard who discovers his magical heritage on his eleventh birthday, when he receives a letter of acceptance to Hogwarts School of Witchcraft and Wizardry. Harry makes close friends and a few enemies during his first year at the school and with the help of his friends, Ron Weasley and Hermione Granger, he faces an attempted comeback by the dark wizard Lord Voldemort, who killed Harry\'s parents, but failed to kill Harry when he was just 15 months old.', 99.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(234, '9781338878936', 'Harry Potter and the Chamber of Secrets', 'The Chamber is flanked with towering pillars entwined with carved wood, and a tall statue of Salazar Slytherin is at the far end. The basilisk rests within the mouth of the statue, possibly symbolising Salazar Slytherin\'s ability to speak Parseltongue. Sometime before 1992, the chamber flooded with water.', 295.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(235, '9781338815283', 'Harry Potter and the Prisoner of Azkaban', 'Harry Potter and the Prisoner of Azkaban is a fantasy novel written by British author J. K. Rowling and is the third in the Harry Potter series. The book follows Harry Potter, a young wizard, in his third year at Hogwarts School of Witchcraft and Wizardry.', 126.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(236, '9780439139595', 'Harry Potter and the Goblet of Fire', 'The novels follow Harry Potter, an 11yearold boy who discovers he is the son of famous wizards and will attend Hogwarts School of Witchcraft and Wizardry. Harry learns of an entire society of wizards and witches. He befriends several Hogwarts students and teachers along his journey.', 337.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(237, '9780439358064', 'Harry Potter and the Order of the Phoenix', 'It follows Harry Potter\'s struggles through his fifth year at Hogwarts School of Witchcraft and Wizardry, including the surreptitious return of the antagonist Lord Voldemort, O.W.L. exams, and an obstructive Ministry of Magic.', 387.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(238, '9781408855652', 'Harry Potter and the HalfBlood Prince', 'owling, J. K. In this book, Harry Potter learns a lot about Lord Voldemort\'s past, and Harry Potter prepares for the final battle against his nemesis with the help of Headmaster Dumbledore. But in that time, Voldemort returns to power, and makes a plan to destroy Harry.', 333.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(239, '9780545010221', 'Harry Potter and the Deathly Hallows', 'The Deathly Hallows is about Harry Potter and his friends finding ways to destroy Voldemort. They learn that even good contains a bit of evil, and vise versa. Even though the trio faces many difficulties, they persevere.', 254.000, 0, 0, 2, 3, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(240, '9780545010221', 'The God Equation', 'The God Equation: The Quest for a Theory of Everything is a popular science book by the futurist and physicist Michio Kaku. The book was initially published on April 6, 2021, by Doubleday. The book debuted at number six on The New York Times nonfiction best-seller list for the week ending April 10, 2021.', 452.000, 0, 0, 2, 15, 4, '2023-11-18 12:52:41', '2023-11-18 12:52:41'),
(241, '9780141439556', 'Đồi gió hú', 'Giới thiệu về Đồi Gió Hú cuốn tiểu thuyết bắt nguồn từ trang viên Yorkshire trên cánh đồng hoang của câu chuyện. Câu chuyện tập trung vào tình yêu trọn vẹn, nồng nàn nhưng cuối cùng cũng phải chịu số phận giữa Catherine Earnshaw và Heathcliff, cũng như việc niềm đam mê không được giải quyết này cuối cùng đã hủy hoại họ và những người xung quanh như thế nào.', 264.000, 0, 0, 3, 1, 1, '2023-11-18 12:32:25', '2023-11-18 12:32:25'),
(242, '9780141439556', 'Wuthering Heights', 'Wuthering Heights is the first and only novel by the English author Emily Brontë, initially published in 1847 under her pen name Ellis Bell.', 412.000, 0, 0, 3, 1, 4, '2023-11-18 12:32:25', '2023-11-18 12:32:25'),
(243, '9780141439518', 'Kiêu hãnh và định kiến', 'Kiêu hãnh và định kiến là tác phẩm nổi tiếng nhất của nhà văn Anh Jane Austen. Tiểu thuyết được viết từ năm 1796 đến năm 1797 và xuất bản năm 1813. Câu chuyện nói về tình yêu và hôn nhân của tầng lớp quý tộc nhỏ tại Anh vào đầu thế kỷ 19.', 384.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03');
INSERT INTO `books` (`id`, `isbn_13`, `title`, `book_description`, `price`, `sale_off`, `views`, `fm_id`, `au_id`, `pub_id`, `creation_date`, `updation_date`) VALUES
(244, '9780141439518', 'Pride and Prejudice', 'Pride and Prejudice is an 1813 novel of manners by English author Jane Austen. The novel follows the character development of Elizabeth Bennet, the protagonist of the book, who learns about the repercussions of hasty judgments and comes to appreciate the difference between superficial goodness and actual goodness.', 317.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03'),
(245, '9780141439587', 'Emma', 'Jane Austen là một tiểu thuyết gia người Anh. Bà nổi tiếng với sáu cuốn tiểu thuyết lấy bối cảnh giới địa chủ trung lưu Anh vào cuối thế kỷ 18. Cốt truyện của Austen thường phản ánh tình cảnh phụ thuộc của người phụ nữ vào hôn nhân như là cứu cánh duy nhất để đảm bảo vị thế xã hội và lợi ích vật chất.', 452.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:03', '2023-11-18 12:33:03'),
(246, '9780141439587', 'Emma', 'Emma is a novel written by English author Jane Austen. It is set in the fictional country village of Highbury and the surrounding estates of Hartfield, Randalls and Donwell Abbey, and involves the relationships among people from a small number of families.', 193.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(247, '9780141439662', 'Sense and Sensibility', 'Sense and Sensibility is the first novel by the English author Jane Austen, published in 1811. It was published anonymously; By A Lady appears on the title page where the author\'s name might have been. It tells the story of the Dashwood sisters, Elinor and Marianne as they come of age.', 223.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(248, '9798861522953', 'Thuyết phục', 'Thuyết phục là cuốn tiểu thuyết cuối cùng được hoàn thành bởi tác giả người Anh Jane Austen. Nó được xuất bản vào ngày 20 tháng 12 năm 1817, cùng với Tu viện Northanger, sáu tháng sau khi bà qua đời, mặc dù trang tiêu đề ghi ngày 1818.', 187.000, 0, 0, 3, 2, 1, '2023-11-18 12:33:53', '2023-11-18 12:33:53'),
(249, '9798861522953', 'Persuasion: Novel', 'Persuasion is the last novel completed by the English author Jane Austen. It was published on 20 December 1817, along with Northanger Abbey, six months after her death, although the title page is dated 1818.', 238.000, 0, 0, 3, 2, 1, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(250, '9781408855652', 'Harry Potter và Hòn đá Phù thủy', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 77.000, 0, 0, 3, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(251, '9781338878936', 'Harry Potter và phòng chứa bí mật', 'Harry Potter và tên tù nhân ngục Azkaban là một quyển tiểu thuyết thuộc thể loại giả tưởng kỳ ảo được viết bởi văn sĩ người Anh J. K. Rowling, đây cũng là tập thứ 3 trong bộ truyện Harry Potter. Quyển sách theo chân Harry Potter, cậu phù thủy nhỏ, trong năm học thứ ba của mình tại Trường Phù thủy và Pháp sư Hogwarts.', 76.000, 0, 0, 3, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(252, '9781338815283', 'Harry Potter Và Tên Tù Nhân Ngục Azkaban', 'Harry Potter và Hòn đá Phù thủy là tiểu thuyết kỳ ảo của văn sĩ người Anh J. K. Rowling. Đây là cuốn đầu trong series tiểu thuyết Harry Potter và là tiểu thuyết đầu tay của J. K. Rowling.', 340.000, 0, 0, 3, 3, 3, '2023-11-18 12:35:05', '2023-11-18 12:35:05'),
(253, '9780439139595', 'Harry Potter Và Chiếc Cốc Lửa', 'Harry Potter và chiếc cốc lửa là một quyển sách thuộc thể loại giả tưởng kỳ ảo được viết bởi tác giả người Anh J. K. Rowling và đây cũng là phần thứ tư trong bộ tiểu thuyết Harry Potter.', 335.000, 0, 0, 3, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(254, '9780439358064', 'Harry Potter Và Hội Phượng Hoàng', 'Harry Potter và hội Phượng hoàng là quyển thứ 5 trong bộ sách Harry Potter của nhà văn J. K. Rowling. Quyển này được đồng loạt xuất bản vào ngày 21 tháng 6 năm 2003 tại Anh, Hoa Kỳ, Canada, Úc và một vài quốc gia khác. Trong ngày đầu tiên xuất bản, nó đã bán được gần 7 triệu cuốn riêng tại Hoa Kỳ và Anh.', 452.000, 0, 0, 3, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(255, '9781408855652', 'Harry Potter Và Hoàng Tử Lai', 'Harry Potter và Hoàng tử lai là quyển sách thứ sáu trong bộ sách giả tưởng nổi tiếng Harry Potter của tác giả J.K. Rowling. Cũng như các quyển trước, nó cũng trở thành một trong những bestseller của năm nó xuất bản.', 135.000, 0, 0, 3, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(256, '9780545010221', 'Harry Potter Và Bảo Bối Tử Thần', 'Harry Potter và Bảo bối Tử thần là cuốn sách thứ bảy và cũng là cuối cùng của bộ tiểu thuyết giả tưởng Harry Potter của nhà văn Anh J.K. Rowling. Nguyên bản tiếng Anh được phát hành đồng thời tại Anh, Hoa Kỳ, Canada và một số nước khác vào ngày 21 tháng 7 năm 2007.', 256.000, 0, 0, 3, 3, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(257, '9781408855652', 'Harry Potter and the Philosopher\'s Stone', 'Harry Potter and the Philosopher\'s Stone is a fantasy novel written by British author J. K. Rowling. The first novel in the Harry Potter series and Rowling\'s debut novel, it follows Harry Potter, a young wizard who discovers his magical heritage on his eleventh birthday, when he receives a letter of acceptance to Hogwarts School of Witchcraft and Wizardry. Harry makes close friends and a few enemies during his first year at the school and with the help of his friends, Ron Weasley and Hermione Granger, he faces an attempted comeback by the dark wizard Lord Voldemort, who killed Harry\'s parents, but failed to kill Harry when he was just 15 months old.', 248.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(258, '9781338878936', 'Harry Potter and the Chamber of Secrets', 'The Chamber is flanked with towering pillars entwined with carved wood, and a tall statue of Salazar Slytherin is at the far end. The basilisk rests within the mouth of the statue, possibly symbolising Salazar Slytherin\'s ability to speak Parseltongue. Sometime before 1992, the chamber flooded with water.', 136.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(259, '9781338815283', 'Harry Potter and the Prisoner of Azkaban', 'Harry Potter and the Prisoner of Azkaban is a fantasy novel written by British author J. K. Rowling and is the third in the Harry Potter series. The book follows Harry Potter, a young wizard, in his third year at Hogwarts School of Witchcraft and Wizardry.', 423.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(260, '9780439139595', 'Harry Potter and the Goblet of Fire', 'The novels follow Harry Potter, an 11yearold boy who discovers he is the son of famous wizards and will attend Hogwarts School of Witchcraft and Wizardry. Harry learns of an entire society of wizards and witches. He befriends several Hogwarts students and teachers along his journey.', 263.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(261, '9780439358064', 'Harry Potter and the Order of the Phoenix', 'It follows Harry Potter\'s struggles through his fifth year at Hogwarts School of Witchcraft and Wizardry, including the surreptitious return of the antagonist Lord Voldemort, O.W.L. exams, and an obstructive Ministry of Magic.', 305.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(262, '9781408855652', 'Harry Potter and the HalfBlood Prince', 'owling, J. K. In this book, Harry Potter learns a lot about Lord Voldemort\'s past, and Harry Potter prepares for the final battle against his nemesis with the help of Headmaster Dumbledore. But in that time, Voldemort returns to power, and makes a plan to destroy Harry.', 172.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(263, '9780545010221', 'Harry Potter and the Deathly Hallows', 'The Deathly Hallows is about Harry Potter and his friends finding ways to destroy Voldemort. They learn that even good contains a bit of evil, and vise versa. Even though the trio faces many difficulties, they persevere.', 367.000, 0, 0, 3, 3, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(264, '9780143136132', 'Bà Dalloway', 'Bà Dalloway là một cuốn tiểu thuyết của Virginia Woolf xuất bản vào ngày 14 tháng 5 năm 1925. Nó kể chi tiết một ngày trong cuộc đời của Clarissa Dalloway, một phụ nữ thuộc tầng lớp thượng lưu hư cấu ở Anh sau Thế chiến thứ nhất. Đây là một trong những tiểu thuyết nổi tiếng nhất của Woolf.', 282.000, 0, 0, 3, 4, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(265, '9780143136132', 'Mrs. Dalloway', 'Dalloway, novel by Virginia Woolf published in 1925. It examines one day in the life of Clarissa Dalloway, an upperclass Londoner married to a member of Parliament. Mrs. Dalloway is essentially plotless; what action there is takes place mainly in the characters consciousness.', 103.000, 0, 0, 3, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(266, '9780143136132', 'Mrs. Dalloway', 'Dalloway, novel by Virginia Woolf published in 1925. It examines one day in the life of Clarissa Dalloway, an upperclass Londoner married to a member of Parliament. Mrs. Dalloway is essentially plotless; what action there is takes place mainly in the characters consciousness.', 320.000, 0, 0, 3, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(267, '9780143131847', 'Frankenstein', 'Frankenstein: hay The Modern Prometheus là một cuốn tiểu thuyết năm 1818 được viết bởi tác giả người Anh Mary Shelley. Frankenstein kể câu chuyện về Victor Frankenstein, một nhà khoa học trẻ, người đã tạo ra một sinh vật có trí tuệ trong một thí nghiệm khoa học không chính thống.', 213.000, 0, 0, 3, 4, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(268, '9780143131847', 'Frankenstein', 'The book tells the story of Victor Frankenstein, a Swiss student of natural science who creates an artificial man from pieces of corpses and brings his creature to life. Though it initially seeks affection, the monster inspires loathing in everyone who meets it.', 289.000, 0, 0, 3, 4, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(269, '9789384225483', 'Ông già và biển cả', 'Ông già và Biển cả là một tiểu thuyết ngắn được Ernest Hemingway viết ở Cuba năm 1951 và xuất bản năm 1952. Nó là truyện ngắn dạng viễn tưởng cuối cùng được Hemingway viết và được xuất bản khi ông còn sống. Đây cũng là tác phẩm nổi tiếng và là một trong những tác phẩm đỉnh cao trong sự nghiệp sáng tác của nhà văn.', 302.000, 0, 0, 3, 6, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(270, '9789384225483', 'The Old Man and the Sea', 'The Old Man and the Sea is a 1952 novella written by the American author Ernest Hemingway. Written between December 1950 and February 1951, it tells the story of Santiago, an aging fisherman who catches a giant marlin after a long struggle, but then loses his bounty to sharks.', 87.000, 0, 0, 3, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(271, '9780684803357', 'Chuông nguyện hồn ai', 'Chuông nguyện hồn ai là tiểu thuyết được xuất bản năm 1940 của nhà văn Mỹ Ernest Miller Hemingway. Cuốn tiểu thuyết viết về Robert Jordan, một người Mỹ thuộc Lữ đoàn quốc tế, tham gia chống Phát xít trong cuộc nội chiến Tây Ban Nha.', 138.000, 0, 0, 3, 6, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(272, '9780684803357', 'For Whom the Bell Tolls', 'For Whom the Bell Tolls is a novel by Ernest Hemingway published in 1940. It tells the story of Robert Jordan, a young American volunteer attached to a Republican guerrilla unit during the Spanish Civil War. As a dynamiter, he is assigned to blow up a bridge during an attack on the city of Segovia.', 321.000, 0, 0, 3, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(273, '9780140447934', 'Chiến tranh và hòa bình', 'Chiến tranh và hòa bình là một tiểu thuyết của Lev Nikolayevich Tolstoy, được xuất bản rải rác trong giai đoạn 1865–1869. Tác phẩm được xem là thành tựu văn học xuất sắc nhất của Tolstoy, cũng như một tác phẩm kinh điển của văn học thế giới.', 457.000, 0, 0, 3, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(274, '9780140447934', 'War and Peace', 'War and Peace is a literary work by Russian author Leo Tolstoy. Set during the Napoleonic Wars, the work mixes fictional narrative with chapters discussing history and philosophy. First published serially beginning in 1865, the work was rewritten and published in its entirety in 1869.', 357.000, 0, 0, 3, 6, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(275, '9780684803357', 'Anna Karenina', 'Anna Karenina là một tiểu thuyết của nhà văn Nga Lev Nikolayevich Tolstoy, được đăng tải nhiều kỳ trên tờ báo Ruskii Vestnik từ năm 1873 đến năm 1877 trước khi xuất bản thành ấn phẩm hoàn chỉnh. Anna Karenina được xem như là một đỉnh cao của tiểu thuyết hiện thực.', 154.000, 0, 0, 3, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(276, '9780684803357', 'Anna Karenina', 'Anna Karenina is a novel by the Russian author Leo Tolstoy, first published in book form in 1878. Considered to be one of the greatest works of literature ever written, Tolstoy himself called it his first true novel.', 380.000, 0, 0, 3, 7, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(277, '9780553210354', 'Cái chết của Ivan Ilyich', 'Cái chết của Ivan Ilyich, xuất bản lần đầu năm 1886, là một tiểu thuyết của Leo Tolstoy, được coi là một trong những kiệt tác của tiểu thuyết cuối cùng của ông, được viết ngay sau khi ông cải đạo vào cuối những năm 1870.', 453.000, 0, 0, 3, 7, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(278, '9780553210354', 'The Death of Ivan Ilyich', 'The Death of Ivan Ilyich, first published in 1886, is a novella by Leo Tolstoy, considered one of the masterpieces of his late fiction, written shortly after his religious conversion of the late 1870s.', 155.000, 0, 0, 3, 7, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(279, '9780486415871', 'Tội ác và hình phạt', 'Tội ác và hình phạt là một tiểu thuyết của nhà văn Nga Fyodor Mikhailovich Dostoyevsky. Tiểu thuyết này cùng với Anh em nhà Karamazov là hai tác phẩm nổi tiếng nhất của Dostoevsky. Tạp chí Times đã bình chọn Tội ác và hình phạt là một trong số những cuốn sách vĩ đại nhất mọi thời đại.', 420.000, 0, 0, 3, 8, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(280, '9780486415871', 'Crime and Punishment', 'Crime and Punishment is a novel by the Russian author Fyodor Dostoevsky. It was first published in the literary journal The Russian Messenger in twelve monthly installments during 1866. It was later published in a single volume.', 115.000, 0, 0, 3, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(281, '9780374528379', 'Anh em nhà Karamazov', 'Anh em nhà Karamazov là tác phẩm cuối cùng của Fyodor Dostoevsky. Fyodor Dostoevsky thuộc loại thiên tài hiếm hoi mà càng về cuối đời thì sự nghiệp sáng tác càng lên tới đỉnh cao hơn. Anh em nhà Karamazov là tác phẩm vĩ đại nhất của ông.', 368.000, 0, 0, 3, 8, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(282, '9780374528379', 'The Brothers Karamazov', 'The Brothers Karamazov, also translated as The Karamazov Brothers, is the last novel by Russian author Fyodor Dostoevsky. Dostoevsky spent nearly two years writing The Brothers Karamazov, which was published as a serial in The Russian Messenger from January 1879 to November 1880.', 369.000, 0, 0, 3, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(283, '9780679734512', 'Ác quỷ', 'Ác quỷ là một cuốn tiểu thuyết của Fyodor Dostoevsky, được xuất bản lần đầu trên tạp chí The Russian Messenger vào năm 1871–72. Nó được coi là một trong bốn kiệt tác được Dostoevsky viết sau khi ông trở về từ cuộc lưu đày ở Siberia, cùng với Tội ác và Trừng phạt, Thằng ngốc và Anh em nhà Karamazov.', 443.000, 0, 0, 3, 8, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(284, '9780679734512', 'Demons (Dostoevsky novel)', 'Demons is a novel by Fyodor Dostoevsky, first published in the journal The Russian Messenger in 1871–72. It is considered one of the four masterworks written by Dostoevsky after his return from Siberian exile, along with Crime and Punishment, The Idiot, and The Brothers Karamazov.', 77.000, 0, 0, 3, 8, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(285, '9781503215672', 'Những cuộc phiêu lưu của Tom Sawyer', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 394.000, 0, 0, 3, 9, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(286, '9781503215672', 'The Adventures of Tom Sawyer', 'The Adventures of Tom Sawyer is an 1876 novel by Mark Twain about a boy growing up along the Mississippi River. It is set in the 1840s in the town of St. Petersburg, which is based on Hannibal, Missouri, where Twain lived as a boy.', 230.000, 0, 0, 3, 9, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(287, '9780143107323', 'Những cuộc phiêu lưu của Huckleberry Finn', 'Những cuộc phiêu lưu của Tom Sawyer là một cuốn tiểu thuyết năm 1876 của Mark Twain về một cậu bé lớn lên dọc theo sông Mississippi. Nó lấy bối cảnh vào những năm 1840 tại thị trấn St. Petersburg, dựa trên Hannibal, Missouri, nơi Twain sống khi còn là một cậu bé.', 353.000, 0, 0, 3, 9, 3, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(288, '9780143107323', 'Adventures of Huckleberry Finn', 'Adventures of Huckleberry Finn is a novel by American author Mark Twain, which was first published in the United Kingdom in December 1884 and in the United States in February 1885.', 263.000, 0, 0, 3, 9, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(289, '9780679722762', 'Ulysses', 'Ulysses là tiểu thuyết gồm 3 phần của James Joyce xuất bản lần đầu năm 1922. Với ẩn dụ về sử thi Odysseus, thủ pháp dòng ý thức và nội dung được nén chặt bằng những sự kiện miên man trôi theo suy tưởng của các nhân vật diễn ra chỉ trong một ngày 16 tháng 6 năm 1904, tác phẩm được tạp chí Time đánh giá là một trong số những kiệt tác văn chương lớn nhất thế giới thế kỷ 20.', 318.000, 0, 0, 3, 10, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(290, '9780679722762', 'Ulysses', 'Ulysses is a modernist novel by Irish writer James Joyce. Parts of it were first serialized in the American journal The Little Review from March 1918 to December 1920, and the entire work was published in Paris by Sylvia Beach on 2 February 1922, Joyce\'s fortieth birthday.', 157.000, 0, 0, 3, 10, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(291, '9780140186475', 'Người Dublin', 'Dubliners là một tuyển tập gồm mười lăm truyện ngắn của James Joyce, được xuất bản lần đầu tiên vào năm 1914. Cuốn sách này mô tả một cách tự nhiên về cuộc sống của tầng lớp trung lưu Ireland trong và xung quanh Dublin trong những năm đầu của thế kỷ 20.', 309.000, 0, 0, 3, 10, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(292, '9780140186475', 'Dubliners', 'Dubliners is a collection of fifteen short stories by James Joyce, first published in 1914. It presents a naturalistic depiction of Irish middle class life in and around Dublin in the early years of the 20th century.', 221.000, 0, 0, 3, 10, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(293, '9780316024976', 'Những kẻ xuất chúng', 'Những kẻ ngoài lề: Câu chuyện thành công là cuốn sách phi hư cấu do Malcolm Gladwell viết và được Little, Brown and Company xuất bản vào ngày 18 tháng 11 năm 2008. Trong Outlier, Gladwell xem xét các yếu tố góp phần vào mức độ thành công cao.', 241.000, 0, 0, 3, 11, 1, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
(294, '9780141036250', 'Outliers: The Story of Success', 'Outliers: The Story of Success is the nonfiction book written by Malcolm Gladwell and published by Little, Brown and Company on November 18, 2008. In Outliers, Gladwell examines the factors that contribute to high levels of success.', 427.000, 0, 0, 3, 11, 4, '2023-11-18 12:38:10', '2023-11-18 12:38:10'),
